const lessonContainer = document.getElementById('lesson-container')
const lessonsGrid = document.getElementById('lessons-grid')
const lessonsContent = {
    lesson1: "<h2>–£—Ä–æ–∫ 1 ‚Äì –°—Ç–≤–æ—Ä–µ–Ω–Ω—è –∫–∞–ª—å–∫—É–ª—è—Ç–æ—Ä–∞: –∑–Ω–∞–π–æ–º—Å—Ç–≤–æ –∑ React, JSX, –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–∞–º–∏ —Ç–∞ —Å—Ç–∞–Ω–æ–º</h2><p>–£ —Ü—å–æ–º—É —É—Ä–æ—Ü—ñ —Å—Ç—É–¥–µ–Ω—Ç–∏ —Å—Ç–≤–æ—Ä—è—Ç—å –ø—Ä–æ—Å—Ç–∏–π –∫–∞–ª—å–∫—É–ª—è—Ç–æ—Ä –Ω–∞ React, —è–∫–∏–π –ø–æ–∫–∞–∂–µ –±–∞–∑–æ–≤—ñ –∫–æ–Ω—Ü–µ–ø—Ü—ñ—ó JSX, —Ñ—É–Ω–∫—Ü—ñ–æ–Ω–∞–ª—å–Ω–∏—Ö –∫–æ–º–ø–æ–Ω–µ–Ω—Ç—ñ–≤, —É–ø—Ä–∞–≤–ª—ñ–Ω–Ω—è —Å—Ç–∞–Ω–æ–º –∑–∞ –¥–æ–ø–æ–º–æ–≥–æ—é <code>useState</code> —Ç–∞ –æ–±—Ä–æ–±–∫–∏ –ø–æ–¥—ñ–π.</p><h3>–û—Å–Ω–æ–≤–Ω—ñ –º–æ–∂–ª–∏–≤–æ—Å—Ç—ñ:</h3><ul><li>–°—Ç–≤–æ—Ä–µ–Ω–Ω—è —Ñ—É–Ω–∫—Ü—ñ–æ–Ω–∞–ª—å–Ω–æ–≥–æ –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–∞.</li><li>–í–∏–∫–æ—Ä–∏—Å—Ç–∞–Ω–Ω—è JSX –¥–ª—è –æ–ø–∏—Å—É UI.</li><li>–ö–µ—Ä—É–≤–∞–Ω–Ω—è –ª–æ–∫–∞–ª—å–Ω–∏–º —Å—Ç–∞–Ω–æ–º —á–µ—Ä–µ–∑ <code>useState</code>.</li><li>–û–±—Ä–æ–±–∫–∞ –ø–æ–¥—ñ–π (–Ω–∞—Ç–∏—Å–∫–∞–Ω–Ω—è –∫–Ω–æ–ø–æ–∫) —ñ –æ–Ω–æ–≤–ª–µ–Ω–Ω—è —ñ–Ω—Ç–µ—Ä—Ñ–µ–π—Å—É.</li></ul><h3>–ü—Ä–∏–∫–ª–∞–¥:</h3><pre><code>import React, { useState } from 'react'; function Calculator(){ const [display, setDisplay] = useState(''); const append = val => setDisplay(prev => prev + val); const clear = () => setDisplay(''); const calc = () => { try { /* –ø—Ä–æ—Å—Ç–∏–π –ø—Ä–∏–∫–ª–∞–¥: eval –ª–∏—à–µ –¥–ª—è –Ω–∞–≤—á–∞–Ω–Ω—è, —É –ø—Ä–æ–¥–∞–∫—à–Ω—ñ ‚Äî –±–µ–∑–ø–µ—á–Ω—ñ –ø–∞—Ä—Å–µ—Ä–∏ */ setDisplay(String(eval(display))); } catch { setDisplay('Error'); } }; return (<div className='calculator'><input readOnly value={display} /><div className='keys'><button onClick={() => append('1')}>1</button><button onClick={() => append('2')}>2</button><button onClick={() => append('3')}>3</button><button onClick={() => append('+')}>+</button><button onClick={() => append('4')}>4</button><button onClick={() => append('5')}>5</button><button onClick={() => append('6')}>6</button><button onClick={() => append('-')}>-</button><button onClick={() => append('7')}>7</button><button onClick={() => append('8')}>8</button><button onClick={() => append('9')}>9</button><button onClick={() => append('*')}>*</button><button onClick={() => append('0')}>0</button><button onClick={clear}>C</button><button onClick={calc}>=</button><button onClick={() => append('/')}>/</button></div></div>); } export default Calculator;</code></pre><p>‚úÖ <strong>–ú–µ—Ç–∞:</strong> —Å—Ç—É–¥–µ–Ω—Ç–∏ –ø–æ–≤–∏–Ω–Ω—ñ –Ω–∞–≤—á–∏—Ç–∏—Å—è —Å—Ç–≤–æ—Ä—é–≤–∞—Ç–∏ —ñ–Ω—Ç–µ—Ä–∞–∫—Ç–∏–≤–Ω—ñ –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–∏ –Ω–∞ React, —Ä–æ–∑—É–º—ñ—Ç–∏ —Ä–æ–ª—å JSX —ñ –ª–æ–∫–∞–ª—å–Ω–æ–≥–æ —Å—Ç–∞–Ω—É, –∞ —Ç–∞–∫–æ–∂ –±–µ–∑–ø–µ—á–Ω–æ –æ–±—Ä–æ–±–ª—è—Ç–∏ –ø–æ–¥—ñ—ó —Ç–∞ –æ–Ω–æ–≤–ª—é–≤–∞—Ç–∏ —ñ–Ω—Ç–µ—Ä—Ñ–µ–π—Å.</p><ul><li>–ü—Ä–∞–∫—Ç–∏—á–Ω–µ: –∑—ñ–±—Ä–∞—Ç–∏ —Ä–æ–±–æ—á–∏–π –∫–∞–ª—å–∫—É–ª—è—Ç–æ—Ä.</li><li>–¢–µ–æ—Ä–µ—Ç–∏—á–Ω–µ: –≤—ñ–¥—Ä—ñ–∑–Ω—è—Ç–∏ props –≤—ñ–¥ state.</li><li>–ë–µ–∑–ø–µ–∫–∞: —á–æ–º—É <code>eval</code> ‚Äî –Ω–µ –¥–ª—è –ø—Ä–æ–¥–∞–∫—à–Ω—É —ñ —è–∫ –π–æ–≥–æ –∑–∞–º—ñ–Ω–∏—Ç–∏.</li></ul><!-- –ë—ñ–±–ª—ñ–æ—Ç–µ–∫–∞: react -->",

    lesson2: "<h2>–£—Ä–æ–∫ 2 ‚Äì Tanstack Query: —Ä–æ–±–æ—Ç–∞ –∑ –∞—Å–∏–Ω—Ö—Ä–æ–Ω–Ω–∏–º–∏ –¥–∞–Ω–∏–º–∏, –∫–µ—à—É–≤–∞–Ω–Ω—è —Ç–∞ –∑–∞–ø–∏—Ç–∏</h2><p>Tanstack Query (—Ä–∞–Ω—ñ—à–µ React Query) ‚Äî —Ü–µ –ø–æ—Ç—É–∂–Ω–∞ –±—ñ–±–ª—ñ–æ—Ç–µ–∫–∞ –¥–ª—è –∫–µ—Ä—É–≤–∞–Ω–Ω—è –∞—Å–∏–Ω—Ö—Ä–æ–Ω–Ω–∏–º–∏ –∑–∞–ø–∏—Ç–∞–º–∏ —Ç–∞ –∫–µ—à–µ–º —É React-–∑–∞—Å—Ç–æ—Å—É–Ω–∫–∞—Ö. –í–æ–Ω–∞ —Å–ø—Ä–æ—â—É—î –æ—Ç—Ä–∏–º–∞–Ω–Ω—è, –æ–Ω–æ–≤–ª–µ–Ω–Ω—è —ñ –ø–æ–≤—Ç–æ—Ä–Ω–µ –≤–∏–∫–æ—Ä–∏—Å—Ç–∞–Ω–Ω—è –¥–∞–Ω–∏—Ö —ñ–∑ —Å–µ—Ä–≤–µ—Ä—ñ–≤ –∞–±–æ API.</p><h3>–û—Å–Ω–æ–≤–Ω—ñ –º–æ–∂–ª–∏–≤–æ—Å—Ç—ñ:</h3><ul><li>–ê–≤—Ç–æ–º–∞—Ç–∏—á–Ω–µ –∫–µ—à—É–≤–∞–Ω–Ω—è —Ä–µ–∑—É–ª—å—Ç–∞—Ç—ñ–≤ –∑–∞–ø–∏—Ç—ñ–≤.</li><li>–ü–æ–≤—Ç–æ—Ä–Ω–µ –æ—Ç—Ä–∏–º–∞–Ω–Ω—è –¥–∞–Ω–∏—Ö –ø—Ä–∏ —Ñ–æ–∫—É—Å—ñ –Ω–∞ –≤—ñ–∫–Ω—ñ.</li><li>–Ü–Ω–¥–∏–∫–∞—Ü—ñ—è —Å—Ç–∞–Ω—ñ–≤ –∑–∞–ø–∏—Ç—É: <code>loading</code>, <code>error</code>, <code>success</code>.</li><li>–û–Ω–æ–≤–ª–µ–Ω–Ω—è –¥–∞–Ω–∏—Ö –±–µ–∑ —Ä—É—á–Ω–æ–≥–æ –≤–∏–∫–ª–∏–∫—É fetch.</li></ul><h3>–ü—Ä–∏–∫–ª–∞–¥:</h3><pre><code>import React from 'react'; import { useQuery, QueryClient, QueryClientProvider } from '@tanstack/react-query'; const client = new QueryClient(); function Todos(){ const { data, error, isLoading } = useQuery({ queryKey: ['todos'], queryFn: async () => { const res = await fetch('https://jsonplaceholder.typicode.com/todos'); return res.json(); } }); if(isLoading) return <p>–ó–∞–≤–∞–Ω—Ç–∞–∂–µ–Ω–Ω—è...</p>; if(error) return <p>–ü–æ–º–∏–ª–∫–∞: {error.message}</p>; return (<ul>{data.slice(0,5).map(todo => <li key={todo.id}>{todo.title}</li>)}</ul>); } export default function App(){ return (<QueryClientProvider client={client}><h2>–°–ø–∏—Å–æ–∫ –∑–∞–≤–¥–∞–Ω—å</h2><Todos /></QueryClientProvider>); }</code></pre><p>‚úÖ <strong>–ú–µ—Ç–∞:</strong> —Å—Ç—É–¥–µ–Ω—Ç–∏ –ø–æ–≤–∏–Ω–Ω—ñ –Ω–∞–≤—á–∏—Ç–∏—Å—è:</p><ul><li>–í–∏–∫–æ—Ä–∏—Å—Ç–æ–≤—É–≤–∞—Ç–∏ <code>useQuery</code> –¥–ª—è –æ—Ç—Ä–∏–º–∞–Ω–Ω—è –¥–∞–Ω–∏—Ö.</li><li>–†–æ–∑—É–º—ñ—Ç–∏ –º–µ—Ö–∞–Ω—ñ–∑–º –∫–µ—à—É–≤–∞–Ω–Ω—è —Ç–∞ –ø–æ–≤—Ç–æ—Ä–Ω–æ–≥–æ –æ—Ç—Ä–∏–º–∞–Ω–Ω—è.</li><li>–ë—É–¥—É–≤–∞—Ç–∏ UI, —è–∫–∏–π —Ä–µ–∞–≥—É—î –Ω–∞ —Ä—ñ–∑–Ω—ñ —Å—Ç–∞–Ω–∏ –∑–∞–ø–∏—Ç—É.</li><li>–ó–∞—Å—Ç–æ—Å–æ–≤—É–≤–∞—Ç–∏ <code>QueryClientProvider</code> –¥–ª—è –≥–ª–æ–±–∞–ª—å–Ω–æ–≥–æ –∫–µ—Ä—É–≤–∞–Ω–Ω—è –¥–∞–Ω–∏–º–∏.</li></ul><!-- –ë—ñ–±–ª—ñ–æ—Ç–µ–∫–∞: @tanstack/react-query -->",

    lesson3: "<h2>–£—Ä–æ–∫ 3 ‚Äì Zustand: –ø—Ä–æ—Å—Ç–∏–π –º–µ–Ω–µ–¥–∂–µ—Ä —Å—Ç–∞–Ω—É —Ç–∞ –ø—Ä–∞–∫—Ç–∏–∫–∞ —Å—Ç–≤–æ—Ä–µ–Ω–Ω—è –≥–ª–æ–±–∞–ª—å–Ω–æ–≥–æ —Å—Ç–∞–Ω—É</h2><p>Zustand ‚Äî —Ü–µ –ª–µ–≥–∫–∏–π –º–µ–Ω–µ–¥–∂–µ—Ä —Å—Ç–∞–Ω—É –¥–ª—è React, —è–∫–∏–π –¥–æ–∑–≤–æ–ª—è—î –ø—Ä–æ—Å—Ç–æ —Å—Ç–≤–æ—Ä—é–≤–∞—Ç–∏ —Ç–∞ –∫–µ—Ä—É–≤–∞—Ç–∏ –≥–ª–æ–±–∞–ª—å–Ω–∏–º–∏ —Å—Ç–∞–Ω–∞–º–∏ –±–µ–∑ –∑–∞–π–≤–æ—ó —Å–∫–ª–∞–¥–Ω–æ—Å—Ç—ñ. –ô–æ–≥–æ —Å–∏–Ω—Ç–∞–∫—Å–∏—Å —ñ–Ω—Ç—É—ó—Ç–∏–≤–Ω–æ –∑—Ä–æ–∑—É–º—ñ–ª–∏–π, –∞ –≤–∏–∫–æ—Ä–∏—Å—Ç–∞–Ω–Ω—è –±–∞–∑—É—î—Ç—å—Å—è –Ω–∞ —Ö—É–∫–∞—Ö.</p><h3>–û—Å–Ω–æ–≤–Ω—ñ –º–æ–∂–ª–∏–≤–æ—Å—Ç—ñ:</h3><ul><li>–ì–ª–æ–±–∞–ª—å–Ω–∏–π —Å—Ç–∞–Ω –±–µ–∑ –∫–æ–Ω—Ç–µ–∫—Å—Ç—ñ–≤ —ñ —Ä–µ–¥ º—é—Å–µ—Ä—ñ–≤.</li><li>–ú—ñ–Ω—ñ–º–∞–ª—ñ—Å—Ç–∏—á–Ω–µ API –¥–ª—è —Å—Ç–≤–æ—Ä–µ–Ω–Ω—è —Å—Ö–æ–≤–∏—â–∞.</li><li>–ü—ñ–¥—Ç—Ä–∏–º–∫–∞ —Å–µ–ª–µ–∫—Ç–æ—Ä—ñ–≤ –¥–ª—è –≤–∏–±—ñ—Ä–∫–æ–≤–æ–≥–æ –æ–Ω–æ–≤–ª–µ–Ω–Ω—è –∫–æ–º–ø–æ–Ω–µ–Ω—Ç—ñ–≤.</li><li>–õ–µ–≥–∫–∞ —ñ–Ω—Ç–µ–≥—Ä–∞—Ü—ñ—è –∑ React Query –∞–±–æ –ª–æ–∫–∞–ª—å–Ω–∏–º —Å—Ö–æ–≤–∏—â–µ–º.</li></ul><h3>–ü—Ä–∏–∫–ª–∞–¥:</h3><pre><code>import { create } from 'zustand';\n\nconst useCounterStore = create((set) => ({\n  count: 0,\n  increment: () => set((state) => ({ count: state.count + 1 })),\n  decrement: () => set((state) => ({ count: state.count - 1 })),\n}));\n\nfunction Counter() {\n  const { count, increment, decrement } = useCounterStore();\n  return (\n    <div>\n      <h2>–õ—ñ—á–∏–ª—å–Ω–∏–∫: {count}</h2>\n      <button onClick={increment}>+</button>\n      <button onClick={decrement}>-</button>\n    </div>\n  );\n}\n\nexport default Counter;</code></pre><p>‚úÖ <strong>–ú–µ—Ç–∞:</strong> —Å—Ç—É–¥–µ–Ω—Ç–∏ –ø–æ–≤–∏–Ω–Ω—ñ –Ω–∞–≤—á–∏—Ç–∏—Å—è:</p><ul><li>–°—Ç–≤–æ—Ä—é–≤–∞—Ç–∏ —Ç–∞ –ø—ñ–¥–∫–ª—é—á–∞—Ç–∏ –≥–ª–æ–±–∞–ª—å–Ω–∏–π —Å—Ç–∞–Ω –∑–∞ –¥–æ–ø–æ–º–æ–≥–æ—é Zustand.</li><li>–ö–µ—Ä—É–≤–∞—Ç–∏ –¥–∞–Ω–∏–º–∏ –±–µ–∑ –≤–∏–∫–æ—Ä–∏—Å—Ç–∞–Ω–Ω—è Redux –∞–±–æ Context API.</li><li>–†–æ–∑—É–º—ñ—Ç–∏ –ø—Ä–∏–Ω—Ü–∏–ø–∏ –æ–Ω–æ–≤–ª–µ–Ω–Ω—è –∫–æ–º–ø–æ–Ω–µ–Ω—Ç—ñ–≤ —á–µ—Ä–µ–∑ —Å–µ–ª–µ–∫—Ç–æ—Ä–∏.</li><li>–ë—É–¥—É–≤–∞—Ç–∏ –ø—Ä–æ—Å—Ç—ñ —Ä–µ–∞–∫—Ç–∏–≤–Ω—ñ —ñ–Ω—Ç–µ—Ä—Ñ–µ–π—Å–∏ –∑ –≥–ª–æ–±–∞–ª—å–Ω–∏–º —Å—Ç–∞–Ω–æ–º.</li></ul><!-- –ë—ñ–±–ª—ñ–æ—Ç–µ–∫–∞: zustand -->",

    lesson4: "<h2>–£—Ä–æ–∫ 4 ‚Äì Todo App –±–µ–∑ —Å–µ—Ä–≤–µ—Ä–∞: –∑–∞–∫—Ä—ñ–ø–ª–µ–Ω–Ω—è Zustand, –ø–µ—Ä–µ–¥–∞—á–∞ –ø—Ä–æ–ø—Å—ñ–≤ —ñ —Å—Ç—Ä—É–∫—Ç—É—Ä–∞ –∫–æ–º–ø–æ–Ω–µ–Ω—Ç—ñ–≤</h2><p>–£ —Ü—å–æ–º—É —É—Ä–æ—Ü—ñ —Å—Ç—É–¥–µ–Ω—Ç–∏ —Å—Ç–≤–æ—Ä—è—Ç—å –Ω–µ–≤–µ–ª–∏–∫–∏–π –∑–∞—Å—Ç–æ—Å—É–Ω–æ–∫ <strong>Todo App</strong> –±–µ–∑ –±–µ–∫–µ–Ω–¥—É, —â–æ–± –∑–∞–∫—Ä—ñ–ø–∏—Ç–∏ –∑–Ω–∞–Ω–Ω—è –ø—Ä–æ Zustand, —Ä–æ–±–æ—Ç—É –∑ –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–∞–º–∏ —Ç–∞ –ø–µ—Ä–µ–¥–∞—á—É –ø—Ä–æ–ø—Å—ñ–≤. –¶–µ —á—É–¥–æ–≤–∞ –ø—Ä–∞–∫—Ç–∏–∫–∞ –¥–ª—è —Å—Ç—Ä—É–∫—Ç—É—Ä—É–≤–∞–Ω–Ω—è –∫–æ–¥—É —ñ —É–ø—Ä–∞–≤–ª—ñ–Ω–Ω—è —Å—Ç–∞–Ω–æ–º –Ω–∞ —Ä—ñ–≤–Ω—ñ –≤—Å—å–æ–≥–æ –∑–∞—Å—Ç–æ—Å—É–Ω–∫—É.</p><h3>–û—Å–Ω–æ–≤–Ω—ñ –º–æ–∂–ª–∏–≤–æ—Å—Ç—ñ:</h3><ul><li>–°—Ç–≤–æ—Ä–µ–Ω–Ω—è –≥–ª–æ–±–∞–ª—å–Ω–æ–≥–æ —Å—Ç–∞–Ω—É –¥–ª—è —Å–ø–∏—Å–∫—É –∑–∞–≤–¥–∞–Ω—å —á–µ—Ä–µ–∑ Zustand.</li><li>–ü–µ—Ä–µ–¥–∞—á–∞ –ø—Ä–æ–ø—Å—ñ–≤ –º—ñ–∂ –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–∞–º–∏.</li><li>–û—Ä–≥–∞–Ω—ñ–∑–∞—Ü—ñ—è —Å—Ç—Ä—É–∫—Ç—É—Ä–∏ –∑–∞—Å—Ç–æ—Å—É–Ω–∫—É –Ω–∞ –∫—ñ–ª—å–∫–∞ –∫–æ–º–ø–æ–Ω–µ–Ω—Ç—ñ–≤.</li><li>–ó–±–µ—Ä–µ–∂–µ–Ω–Ω—è —Å—Ç–∞–Ω—É —É –ø–∞–º º—è—Ç—ñ –±—Ä–∞—É–∑–µ—Ä–∞ (–æ–ø—Ü—ñ–æ–Ω–∞–ª—å–Ω–æ —á–µ—Ä–µ–∑ <code>persist</code> middleware).</li></ul><h3>–ü—Ä–∏–∫–ª–∞–¥:</h3><pre><code>import { create } from 'zustand';\n\nconst useTodoStore = create((set) => ({\n  todos: [],\n  addTodo: (text) => set((state) => ({ todos: [...state.todos, { id: Date.now(), text, done: false }] })),\n  toggleTodo: (id) => set((state) => ({ todos: state.todos.map(t => t.id === id ? { ...t, done: !t.done } : t) })),\n  removeTodo: (id) => set((state) => ({ todos: state.todos.filter(t => t.id !== id) })),\n}));\n\nfunction TodoItem({ todo, onToggle, onRemove }) {\n  return (\n    <li>\n      <span style={{ textDecoration: todo.done ? 'line-through' : 'none' }}>{todo.text}</span>\n      <button onClick={() => onToggle(todo.id)}>‚úì</button>\n      <button onClick={() => onRemove(todo.id)}>‚úï</button>\n    </li>\n  );\n}\n\nfunction TodoList() {\n  const { todos, toggleTodo, removeTodo } = useTodoStore();\n  return <ul>{todos.map(todo => <TodoItem key={todo.id} todo={todo} onToggle={toggleTodo} onRemove={removeTodo} />)}</ul>;\n}\n\nfunction AddTodo() {\n  const [text, setText] = React.useState('');\n  const addTodo = useTodoStore((state) => state.addTodo);\n  return (\n    <div>\n      <input value={text} onChange={(e) => setText(e.target.value)} placeholder='–ù–æ–≤–µ –∑–∞–≤–¥–∞–Ω–Ω—è' />\n      <button onClick={() => { if (text) addTodo(text); setText(''); }}>–î–æ–¥–∞—Ç–∏</button>\n    </div>\n  );\n}\n\nexport default function TodoApp() {\n  return (\n    <div>\n      <h2>–ú—ñ–π Todo App</h2>\n      <AddTodo />\n      <TodoList />\n    </div>\n  );\n}</code></pre><p>‚úÖ <strong>–ú–µ—Ç–∞:</strong> —Å—Ç—É–¥–µ–Ω—Ç–∏ –ø–æ–≤–∏–Ω–Ω—ñ –Ω–∞–≤—á–∏—Ç–∏—Å—è:</p><ul><li>–ó–∞—Å—Ç–æ—Å–æ–≤—É–≤–∞—Ç–∏ Zustand –¥–ª—è —É–ø—Ä–∞–≤–ª—ñ–Ω–Ω—è —Å–ø–∏—Å–∫–∞–º–∏ –¥–∞–Ω–∏—Ö.</li><li>–†–æ–∑–¥—ñ–ª—è—Ç–∏ UI –Ω–∞ –ª–æ–≥—ñ—á–Ω—ñ –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–∏.</li><li>–ü–µ—Ä–µ–¥–∞–≤–∞—Ç–∏ –ø—Ä–æ–ø—Å–∏ –º—ñ–∂ –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–∞–º–∏ –¥–ª—è –≤–∑–∞—î–º–æ–¥—ñ—ó.</li><li>–†–æ–∑—É–º—ñ—Ç–∏, —è–∫ –æ—Ä–≥–∞–Ω—ñ–∑—É–≤–∞—Ç–∏ —Å—Ç—Ä—É–∫—Ç—É—Ä—É –∑–∞—Å—Ç–æ—Å—É–Ω–∫—É –±–µ–∑ —Å–µ—Ä–≤–µ—Ä–∞.</li></ul><!-- –ë—ñ–±–ª—ñ–æ—Ç–µ–∫–∞: zustand, react -->",

    lesson5: "<h2>–£—Ä–æ–∫ 5 ‚Äì ShadCN UI + Redux Toolkit: –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–∏ UI –±—ñ–±–ª—ñ–æ—Ç–µ–∫–∏ —Ç–∞ –±–∞–∑–æ–≤–∏–π –º–µ–Ω–µ–¥–∂–µ—Ä —Å—Ç–∞–Ω—É</h2><p>–£ —Ü—å–æ–º—É —É—Ä–æ—Ü—ñ —Å—Ç—É–¥–µ–Ω—Ç–∏ –ø–æ–∑–Ω–∞–π–æ–º–ª—è—Ç—å—Å—è –∑ <strong>ShadCN UI</strong> ‚Äî —Å—É—á–∞—Å–Ω–æ—é –±—ñ–±–ª—ñ–æ—Ç–µ–∫–æ—é –∫–æ–º–ø–æ–Ω–µ–Ω—Ç—ñ–≤ –¥–ª—è React, –∞ —Ç–∞–∫–æ–∂ –Ω–∞–≤—á–∞—Ç—å—Å—è –≤–∏–∫–æ—Ä–∏—Å—Ç–æ–≤—É–≤–∞—Ç–∏ <strong>Redux Toolkit</strong> –¥–ª—è —É–ø—Ä–∞–≤–ª—ñ–Ω–Ω—è –≥–ª–æ–±–∞–ª—å–Ω–∏–º —Å—Ç–∞–Ω–æ–º. –ü–æ—î–¥–Ω–∞–Ω–Ω—è —Ü–∏—Ö —Ç–µ—Ö–Ω–æ–ª–æ–≥—ñ–π –¥–æ–∑–≤–æ–ª—è—î —Å—Ç–≤–æ—Ä—é–≤–∞—Ç–∏ —Å—Ç–∏–ª—å–Ω—ñ —Ç–∞ –∫–µ—Ä–æ–≤–∞–Ω—ñ –∑–∞—Å—Ç–æ—Å—É–Ω–∫–∏.</p><h3>–û—Å–Ω–æ–≤–Ω—ñ –º–æ–∂–ª–∏–≤–æ—Å—Ç—ñ:</h3><ul><li>–í—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω–Ω—è —Ç–∞ –≤–∏–∫–æ—Ä–∏—Å—Ç–∞–Ω–Ω—è –∫–æ–º–ø–æ–Ω–µ–Ω—Ç—ñ–≤ —ñ–∑ ShadCN UI.</li><li>–°—Ç–≤–æ—Ä–µ–Ω–Ω—è —Å—Ö–æ–≤–∏—â–∞ —Å—Ç–∞–Ω—É –∑–∞ –¥–æ–ø–æ–º–æ–≥–æ—é Redux Toolkit.</li><li>–ü—ñ–¥–∫–ª—é—á–µ–Ω–Ω—è <code>Provider</code> –¥–ª—è –¥–æ—Å—Ç—É–ø—É –¥–æ —Å—Ç–∞–Ω—É –≤ —É—Å—å–æ–º—É –∑–∞—Å—Ç–æ—Å—É–Ω–∫—É.</li><li>–ó–≤ º—è–∑–æ–∫ UI –∑ Redux —á–µ—Ä–µ–∑ <code>useSelector</code> —ñ <code>useDispatch</code>.</li></ul><h3>–ü—Ä–∏–∫–ª–∞–¥:</h3><pre><code>import React from 'react';\nimport { Provider, useDispatch, useSelector } from 'react-redux';\nimport { configureStore, createSlice } from '@reduxjs/toolkit';\nimport { Button } from '@/components/ui/button';\n\nconst counterSlice = createSlice({\n  name: 'counter',\n  initialState: { value: 0 },\n  reducers: {\n    increment: (state) => { state.value += 1; },\n    decrement: (state) => { state.value -= 1; }\n  }\n});\n\nconst store = configureStore({ reducer: { counter: counterSlice.reducer } });\n\nfunction Counter() {\n  const dispatch = useDispatch();\n  const count = useSelector((state) => state.counter.value);\n  return (\n    <div className='flex flex-col items-center gap-2 p-4'>\n      <h2 className='text-xl font-semibold'>–õ—ñ—á–∏–ª—å–Ω–∏–∫: {count}</h2>\n      <div className='flex gap-2'>\n        <Button onClick={() => dispatch(counterSlice.actions.increment())}>+</Button>\n        <Button onClick={() => dispatch(counterSlice.actions.decrement())}>-</Button>\n      </div>\n    </div>\n  );\n}\n\nexport default function App() {\n  return (\n    <Provider store={store}>\n      <div className='p-6 max-w-sm mx-auto'>\n        <h1 className='text-2xl font-bold mb-4'>ShadCN + Redux Toolkit</h1>\n        <Counter />\n      </div>\n    </Provider>\n  );\n}</code></pre><p>‚úÖ <strong>–ú–µ—Ç–∞:</strong> —Å—Ç—É–¥–µ–Ω—Ç–∏ –ø–æ–≤–∏–Ω–Ω—ñ –Ω–∞–≤—á–∏—Ç–∏—Å—è:</p><ul><li>–ü—ñ–¥–∫–ª—é—á–∞—Ç–∏ ShadCN UI —Ç–∞ –≤–∏–∫–æ—Ä–∏—Å—Ç–æ–≤—É–≤–∞—Ç–∏ –π–æ–≥–æ –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–∏.</li><li>–ù–∞–ª–∞—à—Ç–æ–≤—É–≤–∞—Ç–∏ Redux Toolkit –¥–ª—è —É–ø—Ä–∞–≤–ª—ñ–Ω–Ω—è —Å—Ç–∞–Ω–æ–º.</li><li>–í–∏–∫–æ—Ä–∏—Å—Ç–æ–≤—É–≤–∞—Ç–∏ <code>Provider</code>, <code>useSelector</code> —ñ <code>useDispatch</code>.</li><li>–ü–æ—î–¥–Ω—É–≤–∞—Ç–∏ —Å—É—á–∞—Å–Ω–∏–π UI –∑ –∫–µ—Ä—É–≤–∞–Ω–Ω—è–º —Å—Ç–∞–Ω–æ–º —É React-–∑–∞—Å—Ç–æ—Å—É–Ω–∫–∞—Ö.</li></ul><!-- –ë—ñ–±–ª—ñ–æ—Ç–µ–∫–∏: shadcn/ui, @reduxjs/toolkit, react-redux -->",

    lesson6: "<h2>–£—Ä–æ–∫ 6‚Äì7 ‚Äì Watchlist App: —Å—Ç–≤–æ—Ä–µ–Ω–Ω—è –∑–∞—Å—Ç–æ—Å—É–Ω–∫—É –∑—ñ —Å–ø–∏—Å–∫–æ–º —Ñ—ñ–ª—å–º—ñ–≤ —Ç–∞ –∞–Ω—ñ–º–∞—Ü—ñ—è–º–∏ —á–µ—Ä–µ–∑ Framer Motion</h2><p>–£ —Ü—å–æ–º—É —É—Ä–æ—Ü—ñ —Å—Ç—É–¥–µ–Ω—Ç–∏ —Å—Ç–≤–æ—Ä—è—Ç—å <strong>Watchlist App</strong> ‚Äî –∑–∞—Å—Ç–æ—Å—É–Ω–æ–∫ –¥–ª—è –∑–±–µ—Ä–µ–∂–µ–Ω–Ω—è —É–ª—é–±–ª–µ–Ω–∏—Ö —Ñ—ñ–ª—å–º—ñ–≤ —É —Å–ø–∏—Å–æ–∫ –ø–µ—Ä–µ–≥–ª—è–¥—É. –û—Å–Ω–æ–≤–Ω–∞ –º–µ—Ç–∞ ‚Äî –Ω–∞–≤—á–∏—Ç–∏—Å—è –ø–æ—î–¥–Ω—É–≤–∞—Ç–∏ –∫–µ—Ä—É–≤–∞–Ω–Ω—è —Å—Ç–∞–Ω–æ–º —ñ–∑ –≤—ñ–∑—É–∞–ª—å–Ω–∏–º–∏ –∞–Ω—ñ–º–∞—Ü—ñ—è–º–∏ –∑–∞ –¥–æ–ø–æ–º–æ–≥–æ—é <strong>Framer Motion</strong>.</p><h3>–û—Å–Ω–æ–≤–Ω—ñ –º–æ–∂–ª–∏–≤–æ—Å—Ç—ñ:</h3><ul><li>–°—Ç–≤–æ—Ä–µ–Ω–Ω—è —Å–ø–∏—Å–∫—É —Ñ—ñ–ª—å–º—ñ–≤ (watchlist) —ñ–∑ –º–æ–∂–ª–∏–≤—ñ—Å—Ç—é –¥–æ–¥–∞–≤–∞–Ω–Ω—è —Ç–∞ –≤–∏–¥–∞–ª–µ–Ω–Ω—è.</li><li>–ê–Ω—ñ–º–∞—Ü—ñ—è –ø–æ—è–≤–∏, –≤–∏–¥–∞–ª–µ–Ω–Ω—è —Ç–∞ –Ω–∞–≤–µ–¥–µ–Ω–Ω—è –µ–ª–µ–º–µ–Ω—Ç—ñ–≤ —á–µ—Ä–µ–∑ <code>Framer Motion</code>.</li><li>–ó–±–µ—Ä—ñ–≥–∞–Ω–Ω—è —Å–ø–∏—Å–∫—É —É –ª–æ–∫–∞–ª—å–Ω–æ–º—É —Å—Ç–∞–Ω—ñ –∞–±–æ Zustand.</li><li>–ü—Ä–∞–∫—Ç–∏–∫–∞ —Å—Ç–≤–æ—Ä–µ–Ω–Ω—è –∫–æ–º–ø–æ–Ω–µ–Ω—Ç—ñ–≤ —ñ –ø–æ–±—É–¥–æ–≤–∏ –¥–∏–Ω–∞–º—ñ—á–Ω–æ–≥–æ —ñ–Ω—Ç–µ—Ä—Ñ–µ–π—Å—É.</li></ul><h3>–ü—Ä–∏–∫–ª–∞–¥:</h3><pre><code>import React from 'react';\nimport { motion, AnimatePresence } from 'framer-motion';\nimport { create } from 'zustand';\n\nconst useWatchlist = create((set) => ({\n  movies: [],\n  addMovie: (title) => set((state) => ({ movies: [...state.movies, { id: Date.now(), title }] })),\n  removeMovie: (id) => set((state) => ({ movies: state.movies.filter(m => m.id !== id) })),\n}));\n\nfunction MovieItem({ movie, onRemove }) {\n  return (\n    <motion.li layout initial={{ opacity: 0, y: 20 }} animate={{ opacity: 1, y: 0 }} exit={{ opacity: 0, x: -50 }} className='flex justify-between items-center p-2 border-b'>\n      <span>{movie.title}</span>\n      <button onClick={() => onRemove(movie.id)}>‚úï</button>\n    </motion.li>\n  );\n}\n\nfunction Watchlist() {\n  const { movies, removeMovie } = useWatchlist();\n  return (\n    <ul className='w-full max-w-md mx-auto'>\n      <AnimatePresence>\n        {movies.map((m) => (\n          <MovieItem key={m.id} movie={m} onRemove={removeMovie} />\n        ))}\n      </AnimatePresence>\n    </ul>\n  );\n}\n\nfunction AddMovie() {\n  const [title, setTitle] = React.useState('');\n  const addMovie = useWatchlist((state) => state.addMovie);\n  return (\n    <div className='flex gap-2 justify-center mb-4'>\n      <input className='border p-1 rounded' value={title} onChange={(e) => setTitle(e.target.value)} placeholder='–ù–∞–∑–≤–∞ —Ñ—ñ–ª—å–º—É' />\n      <button onClick={() => { if (title) addMovie(title); setTitle(''); }}>–î–æ–¥–∞—Ç–∏</button>\n    </div>\n  );\n}\n\nexport default function WatchlistApp() {\n  return (\n    <div className='p-6 text-center'>\n      <h2 className='text-2xl font-bold mb-4'>üé¨ Watchlist App</h2>\n      <AddMovie />\n      <Watchlist />\n    </div>\n  );\n}</code></pre><p>‚úÖ <strong>–ú–µ—Ç–∞:</strong> —Å—Ç—É–¥–µ–Ω—Ç–∏ –ø–æ–≤–∏–Ω–Ω—ñ –Ω–∞–≤—á–∏—Ç–∏—Å—è:</p><ul><li>–°—Ç–≤–æ—Ä—é–≤–∞—Ç–∏ —ñ–Ω—Ç–µ—Ä–∞–∫—Ç–∏–≤–Ω—ñ —Å–ø–∏—Å–∫–∏ –∑ –∞–Ω—ñ–º–∞—Ü—ñ—î—é –∑–∞ –¥–æ–ø–æ–º–æ–≥–æ—é Framer Motion.</li><li>–í–∏–∫–æ—Ä–∏—Å—Ç–æ–≤—É–≤–∞—Ç–∏ Zustand –∞–±–æ –ª–æ–∫–∞–ª—å–Ω–∏–π —Å—Ç–∞–Ω –¥–ª—è –∫–µ—Ä—É–≤–∞–Ω–Ω—è –¥–∞–Ω–∏–º–∏.</li><li>–†–æ–∑—É–º—ñ—Ç–∏ –ø—Ä–∏–Ω—Ü–∏–ø <em>AnimatePresence</em> –¥–ª—è –ø–ª–∞–≤–Ω–æ–≥–æ –¥–æ–¥–∞–≤–∞–Ω–Ω—è/–≤–∏–¥–∞–ª–µ–Ω–Ω—è –µ–ª–µ–º–µ–Ω—Ç—ñ–≤.</li><li>–ü–æ—î–¥–Ω—É–≤–∞—Ç–∏ —Ñ—É–Ω–∫—Ü—ñ–æ–Ω–∞–ª—å–Ω—ñ—Å—Ç—å —ñ –≤—ñ–∑—É–∞–ª—å–Ω—ñ –µ—Ñ–µ–∫—Ç–∏ –≤ –æ–¥–Ω–æ–º—É –∑–∞—Å—Ç–æ—Å—É–Ω–∫—É.</li></ul><!-- –ë—ñ–±–ª—ñ–æ—Ç–µ–∫–∏: framer-motion, zustand, react -->",

    lesson7: "<h2>–£—Ä–æ–∫ 8‚Äì9 ‚Äì Task Manager: drag-and-drop —Ñ—É–Ω–∫—Ü—ñ–æ–Ω–∞–ª —ñ–∑ dnd-kit —Ç–∞ –ø–µ—Ä–µ–º—ñ—â–µ–Ω–Ω—è –∑–∞–¥–∞—á –º—ñ–∂ —Å–ø–∏—Å–∫–∞–º–∏</h2><p>–£ —Ü—å–æ–º—É —É—Ä–æ—Ü—ñ —Å—Ç—É–¥–µ–Ω—Ç–∏ —Å—Ç–≤–æ—Ä—è—Ç—å <strong>Task Manager</strong> —ñ–∑ –ø—ñ–¥—Ç—Ä–∏–º–∫–æ—é drag-and-drop –¥–ª—è –ø–µ—Ä–µ–º—ñ—â–µ–Ω–Ω—è –∑–∞–¥–∞—á –º—ñ–∂ —Ä—ñ–∑–Ω–∏–º–∏ –∫–æ–ª–æ–Ω–∫–∞–º–∏ (–Ω–∞–ø—Ä–∏–∫–ª–∞–¥, To Do, In Progress, Done). –î–ª—è —Ü—å–æ–≥–æ –≤–∏–∫–æ—Ä–∏—Å—Ç–æ–≤—É—î—Ç—å—Å—è —Å—É—á–∞—Å–Ω–∞ –±—ñ–±–ª—ñ–æ—Ç–µ–∫–∞ <strong>dnd-kit</strong>, —è–∫–∞ –Ω–∞–¥–∞—î –ø—Ä–æ—Å—Ç–∏–π API –¥–ª—è –ø–æ–±—É–¥–æ–≤–∏ –≥–Ω—É—á–∫–∏—Ö —Å–∏—Å—Ç–µ–º –ø–µ—Ä–µ—Ç—è–≥—É–≤–∞–Ω–Ω—è.</p><h3>–û—Å–Ω–æ–≤–Ω—ñ –º–æ–∂–ª–∏–≤–æ—Å—Ç—ñ:</h3><ul><li>–†–µ–∞–ª—ñ–∑–∞—Ü—ñ—è drag-and-drop —Ñ—É–Ω–∫—Ü—ñ–æ–Ω–∞–ª—É –∑–∞ –¥–æ–ø–æ–º–æ–≥–æ—é dnd-kit.</li><li>–ü–µ—Ä–µ–º—ñ—â–µ–Ω–Ω—è –∑–∞–¥–∞—á –º—ñ–∂ —Å–ø–∏—Å–∫–∞–º–∏.</li><li>–£–ø—Ä–∞–≤–ª—ñ–Ω–Ω—è —Å—Ç–∞–Ω–æ–º —á–µ—Ä–µ–∑ Zustand –∞–±–æ –ª–æ–∫–∞–ª—å–Ω–∏–π state.</li><li>–î–∏–Ω–∞–º—ñ—á–Ω–∞ –≤—ñ–∑—É–∞–ª—å–Ω–∞ –≤–∑–∞—î–º–æ–¥—ñ—è –±–µ–∑ —Å—Ç–æ—Ä–æ–Ω–Ω—å–æ–≥–æ —Å–µ—Ä–≤–µ—Ä–∞.</li></ul><h3>–ü—Ä–∏–∫–ª–∞–¥:</h3><pre><code>import React from 'react';\nimport { DndContext, closestCenter } from '@dnd-kit/core';\nimport { arrayMove, SortableContext, useSortable, verticalListSortingStrategy } from '@dnd-kit/sortable';\nimport { CSS } from '@dnd-kit/utilities';\nimport { create } from 'zustand';\n\nconst useTaskStore = create((set) => ({\n  columns: {\n    todo: ['–ü—ñ–¥–≥–æ—Ç—É–≤–∞—Ç–∏ –∑–≤—ñ—Ç', '–ó—Ä–æ–±–∏—Ç–∏ —Ä–µ–≤ º—é –∫–æ–¥—É'],\n    progress: ['–°—Ç–≤–æ—Ä–∏—Ç–∏ –º–∞–∫–µ—Ç UI'],\n    done: ['–ù–∞–ª–∞—à—Ç—É–≤–∞—Ç–∏ ESLint']\n  },\n  moveTask: (from, to, task) => set((state) => {\n    const newFrom = state.columns[from].filter((t) => t !== task);\n    const newTo = [...state.columns[to], task];\n    return { columns: { ...state.columns, [from]: newFrom, [to]: newTo } };\n  })\n}));\n\nfunction SortableItem({ id }) {\n  const { attributes, listeners, setNodeRef, transform, transition } = useSortable({ id });\n  const style = { transform: CSS.Transform.toString(transform), transition };\n  return <div ref={setNodeRef} style={style} {...attributes} {...listeners} className='p-2 bg-white rounded shadow mb-2 cursor-grab'>{id}</div>;\n}\n\nfunction Column({ id, tasks }) {\n  return (\n    <div className='w-1/3 p-3 bg-gray-100 rounded'>\n      <h3 className='font-semibold text-center mb-2'>{id.toUpperCase()}</h3>\n      <SortableContext items={tasks} strategy={verticalListSortingStrategy}>\n        {tasks.map((task) => (<SortableItem key={task} id={task} />))}\n      </SortableContext>\n    </div>\n  );\n}\n\nexport default function TaskManager() {\n  const { columns, moveTask } = useTaskStore();\n\n  const handleDragEnd = (event) => {\n    const { active, over } = event;\n    if (!over) return;\n    const from = Object.keys(columns).find((key) => columns[key].includes(active.id));\n    const to = over.id;\n    if (from && to && from !== to) moveTask(from, to, active.id);\n  };\n\n  return (\n    <div className='p-6'>\n      <h2 className='text-2xl font-bold text-center mb-4'>üß© Task Manager</h2>\n      <DndContext collisionDetection={closestCenter} onDragEnd={handleDragEnd}>\n        <div className='flex gap-4 justify-center'>\n          {Object.entries(columns).map(([key, tasks]) => (\n            <div key={key}>\n              <Column id={key} tasks={tasks} />\n            </div>\n          ))}\n        </div>\n      </DndContext>\n    </div>\n  );\n}</code></pre><p>‚úÖ <strong>–ú–µ—Ç–∞:</strong> —Å—Ç—É–¥–µ–Ω—Ç–∏ –ø–æ–≤–∏–Ω–Ω—ñ –Ω–∞–≤—á–∏—Ç–∏—Å—è:</p><ul><li>–í–∏–∫–æ—Ä–∏—Å—Ç–æ–≤—É–≤–∞—Ç–∏ –±—ñ–±–ª—ñ–æ—Ç–µ–∫—É dnd-kit –¥–ª—è drag-and-drop —É React.</li><li>–†–æ–∑—É–º—ñ—Ç–∏ –ø—Ä–∏–Ω—Ü–∏–ø–∏ —Å–æ—Ä—Ç—É–≤–∞–Ω–Ω—è —Ç–∞ –ø–µ—Ä–µ–º—ñ—â–µ–Ω–Ω—è –µ–ª–µ–º–µ–Ω—Ç—ñ–≤ –º—ñ–∂ —Å–ø–∏—Å–∫–∞–º–∏.</li><li>–Ü–Ω—Ç–µ–≥—Ä—É–≤–∞—Ç–∏ drag-and-drop —ñ–∑ –≥–ª–æ–±–∞–ª—å–Ω–∏–º —Å—Ç–∞–Ω–æ–º (Zustand).</li><li>–°—Ç–≤–æ—Ä—é–≤–∞—Ç–∏ —ñ–Ω—Ç–µ—Ä–∞–∫—Ç–∏–≤–Ω—ñ —ñ–Ω—Ç–µ—Ä—Ñ–µ–π—Å–∏ –∑ –≤—ñ–∑—É–∞–ª—å–Ω–∏–º —Ñ—ñ–¥–±–µ–∫–æ–º.</li></ul><!-- –ë—ñ–±–ª—ñ–æ—Ç–µ–∫–∏: @dnd-kit/core, @dnd-kit/sortable, zustand, react -->",

    lesson8: '<h2>–£—Ä–æ–∫ 10 ‚Äì –¢–µ–æ—Ä–µ—Ç–∏—á–Ω–∏–π —Ç–µ—Å—Ç –ø–æ —É—Ä–æ–∫–∞—Ö 1‚Äì9: –ø–µ—Ä–µ–≤—ñ—Ä–∫–∞ –∑–Ω–∞–Ω—å –ø–µ—Ä–µ–¥ —Ñ—ñ–Ω–∞–ª—å–Ω–∏–º –µ—Ç–∞–ø–æ–º</h2><p>–¶–µ–π —É—Ä–æ–∫ –ø—Ä–∏—Å–≤—è—á–µ–Ω–∏–π –ø—ñ–¥—Å—É–º–∫–æ–≤—ñ–π –ø–µ—Ä–µ–≤—ñ—Ä—Ü—ñ —Ç–µ–æ—Ä–µ—Ç–∏—á–Ω–∏—Ö –∑–Ω–∞–Ω—å, –æ—Ç—Ä–∏–º–∞–Ω–∏—Ö —É –ø–æ–ø–µ—Ä–µ–¥–Ω—ñ—Ö —Ç–µ–º–∞—Ö –∫—É—Ä—Å—É. –°—Ç—É–¥–µ–Ω—Ç–∏ –º–∞—é—Ç—å –ø—Ä–æ–¥–µ–º–æ–Ω—Å—Ç—Ä—É–≤–∞—Ç–∏ —Ä–æ–∑—É–º—ñ–Ω–Ω—è –∫–ª—é—á–æ–≤–∏—Ö –∫–æ–Ω—Ü–µ–ø—Ü—ñ–π React, —Å—Ç–∞–Ω—É, –∑–∞–ø–∏—Ç—ñ–≤, UI-–∫–æ–º–ø–æ–Ω–µ–Ω—Ç—ñ–≤ —Ç–∞ —ñ–Ω—Å—Ç—Ä—É–º–µ–Ω—Ç—ñ–≤ –æ–ø—Ç–∏–º—ñ–∑–∞—Ü—ñ—ó –∫–æ–¥—É.</p><h3>–û—Å–Ω–æ–≤–Ω—ñ —Ç–µ–º–∏ –ø–µ—Ä–µ–≤—ñ—Ä–∫–∏:</h3><ul><li>–î–µ–∫–æ—Ä–∞—Ç–æ—Ä–∏ —Ç–∞ –æ–ø—Ç–∏–º—ñ–∑–∞—Ü—ñ—è –∫–æ–¥—É Python.</li><li>React: –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–∏, JSX, —Å—Ç–∞–Ω (<code>useState</code>).</li><li>Tanstack Query: —Ä–æ–±–æ—Ç–∞ –∑ –∞—Å–∏–Ω—Ö—Ä–æ–Ω–Ω–∏–º–∏ –¥–∞–Ω–∏–º–∏ —Ç–∞ –∫–µ—à—É–≤–∞–Ω–Ω—è.</li><li>Zustand: —Å—Ç–≤–æ—Ä–µ–Ω–Ω—è –≥–ª–æ–±–∞–ª—å–Ω–æ–≥–æ —Å—Ç–∞–Ω—É —Ç–∞ —É–ø—Ä–∞–≤–ª—ñ–Ω–Ω—è –Ω–∏–º.</li><li>–ü—Ä–∞–∫—Ç–∏–∫–∞ Todo App –±–µ–∑ —Å–µ—Ä–≤–µ—Ä–∞.</li><li>ShadCN UI + Redux Toolkit: –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–∏ —Ç–∞ state management.</li><li>Framer Motion: —Å—Ç–≤–æ—Ä–µ–Ω–Ω—è –∞–Ω—ñ–º–∞—Ü—ñ–π —É React.</li><li>dnd-kit: —Ä–µ–∞–ª—ñ–∑–∞—Ü—ñ—è drag-and-drop —É Task Manager.</li></ul><h3>–ü—Ä–∏–∫–ª–∞–¥ —Ñ–æ—Ä–º–∞—Ç—É –∑–∞–ø–∏—Ç–∞–Ω—å:</h3><pre><code>1. –©–æ —Ç–∞–∫–µ –¥–µ–∫–æ—Ä–∞—Ç–æ—Ä —É Python —ñ –¥–ª—è —á–æ–≥–æ –≤—ñ–Ω –≤–∏–∫–æ—Ä–∏—Å—Ç–æ–≤—É—î—Ç—å—Å—è?\n2. –Ø–∫–∞ —Ä—ñ–∑–Ω–∏—Ü—è –º—ñ–∂ props —ñ state —É React?\n3. –Ø–∫ Tanstack Query –∫–µ—à—É—î –æ—Ç—Ä–∏–º–∞–Ω—ñ –¥–∞–Ω—ñ?\n4. –Ø–∫ —Å—Ç–≤–æ—Ä–∏—Ç–∏ –≥–ª–æ–±–∞–ª—å–Ω–∏–π —Å—Ç–∞–Ω –∑–∞ –¥–æ–ø–æ–º–æ–≥–æ—é Zustand?\n5. –Ø–∫—É —Ä–æ–ª—å –≤—ñ–¥—ñ–≥—Ä–∞—î Provider —É Redux Toolkit?\n6. –î–ª—è —á–æ–≥–æ –≤–∏–∫–æ—Ä–∏—Å—Ç–æ–≤—É—î—Ç—å—Å—è –∫–æ–º–ø–æ–Ω–µ–Ω—Ç AnimatePresence —É Framer Motion?\n7. –©–æ –∑–∞–±–µ–∑–ø–µ—á—É—î –±—ñ–±–ª—ñ–æ—Ç–µ–∫–∞ dnd-kit?</code></pre><p>‚úÖ <strong>–ú–µ—Ç–∞:</strong> —Å—Ç—É–¥–µ–Ω—Ç–∏ –ø–æ–≤–∏–Ω–Ω—ñ:</p><ul><li>–ü–æ–≤—Ç–æ—Ä–∏—Ç–∏ –æ—Å–Ω–æ–≤–Ω—ñ —Ç–µ–æ—Ä–µ—Ç–∏—á–Ω—ñ –∞—Å–ø–µ–∫—Ç–∏ –ø–æ–ø–µ—Ä–µ–¥–Ω—ñ—Ö —Ç–µ–º.</li><li>–ó—Ä–æ–∑—É–º—ñ—Ç–∏, —è–∫ –ø–æ—î–¥–Ω—É—é—Ç—å—Å—è —ñ–Ω—Å—Ç—Ä—É–º–µ–Ω—Ç–∏ —É —Å—É—á–∞—Å–Ω–æ–º—É —Ñ—Ä–æ–Ω—Ç–µ–Ω–¥-—Ä–æ–∑—Ä–æ–±–ª–µ–Ω–Ω—ñ.</li><li>–ü—ñ–¥–≥–æ—Ç—É–≤–∞—Ç–∏—Å—è –¥–æ —Ñ—ñ–Ω–∞–ª—å–Ω–æ–≥–æ –ø—Ä–∞–∫—Ç–∏—á–Ω–æ–≥–æ –ø—Ä–æ—î–∫—Ç—É.</li><li>–ù–∞–≤—á–∏—Ç–∏—Å—è –∞—Ä–≥—É–º–µ–Ω—Ç–æ–≤–∞–Ω–æ –ø–æ—è—Å–Ω—é–≤–∞—Ç–∏ –ø—Ä–∏–Ω—Ü–∏–ø–∏ —Ä–æ–±–æ—Ç–∏ –±—ñ–±–ª—ñ–æ—Ç–µ–∫ —ñ —Ç–µ—Ö–Ω–æ–ª–æ–≥—ñ–π.</li></ul><!-- –¢–∏–ø —É—Ä–æ–∫—É: —Ç–µ–æ—Ä–µ—Ç–∏—á–Ω–∏–π —Ç–µ—Å—Ç, –æ—Ö–æ–ø–ª–µ–Ω–Ω—è —Ç–µ–º 1‚Äì9 -->',

    lesson9: "<h2>–£—Ä–æ–∫ 11‚Äì12 ‚Äì Product List App: —Ñ—ñ–ª—å—Ç—Ä–∞—Ü—ñ—è, —Å–æ—Ä—Ç—É–≤–∞–Ω–Ω—è —Ç–∞ –ø–æ—à—É–∫ –ø–æ —Å–ø–∏—Å–∫—É —Ç–æ–≤–∞—Ä—ñ–≤ –Ω–∞ —Ñ—Ä–æ–Ω—Ç–µ–Ω–¥—ñ</h2><p>–£ —Ü—å–æ–º—É —É—Ä–æ—Ü—ñ —Å—Ç—É–¥–µ–Ω—Ç–∏ —Å—Ç–≤–æ—Ä—è—Ç—å —ñ–Ω—Ç–µ—Ä—Ñ–µ–π—Å –¥–ª—è –≤—ñ–¥–æ–±—Ä–∞–∂–µ–Ω–Ω—è —Å–ø–∏—Å–∫—É –ø—Ä–æ–¥—É–∫—Ç—ñ–≤ –∑ –º–æ–∂–ª–∏–≤—ñ—Å—Ç—é —Ñ—ñ–ª—å—Ç—Ä–∞—Ü—ñ—ó –∑–∞ –∫–∞—Ç–µ–≥–æ—Ä—ñ—è–º–∏, —Å–æ—Ä—Ç—É–≤–∞–Ω–Ω—è –∑–∞ —Ü—ñ–Ω–æ—é/—Ä–µ–π—Ç–∏–Ω“ë–æ–º —ñ –ø–æ—à—É–∫—É –∑–∞ –Ω–∞–∑–≤–æ—é. –û—Å–Ω–æ–≤–Ω–∞ —É–≤–∞–≥–∞ ‚Äî –æ–ø—Ç–∏–º—ñ–∑–∞—Ü—ñ—è –æ–Ω–æ–≤–ª–µ–Ω—å UI, —Ä–æ–±–æ—Ç–∞ –∑ –ª–æ–∫–∞–ª—å–Ω–∏–º —Å—Ç–∞–Ω–æ–º —Ç–∞ –≤–∏–∫–æ—Ä–∏—Å—Ç–∞–Ω–Ω—è <code>useMemo</code> –¥–ª—è —É–Ω–∏–∫–Ω–µ–Ω–Ω—è –∑–∞–π–≤–∏—Ö –ø–µ—Ä–µ—Ä–µ–Ω–¥–µ—Ä—ñ–≤.</p><h3>–û—Å–Ω–æ–≤–Ω—ñ –º–æ–∂–ª–∏–≤–æ—Å—Ç—ñ:</h3><ul><li>–ü–æ–±—É–¥–æ–≤–∞ —Å–ø–∏—Å–∫—É —Ç–æ–≤–∞—Ä—ñ–≤ –∑ –¥–∞–Ω–∏—Ö —É –ª–æ–∫–∞–ª—å–Ω–æ–º—É –º–∞—Å–∏–≤—ñ (–º–æ–∂–ª–∏–≤–æ ‚Äî mock API).</li><li>–ü–æ—à—É–∫ –ø–æ –Ω–∞–∑–≤—ñ –∑ debounce –¥–ª—è –∑–º–µ–Ω—à–µ–Ω–Ω—è –∫—ñ–ª—å–∫–æ—Å—Ç—ñ –æ–ø–µ—Ä–∞—Ü—ñ–π.</li><li>–§—ñ–ª—å—Ç—Ä–∞—Ü—ñ—è –∑–∞ –∫–∞—Ç–µ–≥–æ—Ä—ñ—î—é —Ç–∞ —Å–æ—Ä—Ç—É–≤–∞–Ω–Ω—è (–∑–∞ –∑—Ä–æ—Å—Ç–∞–Ω–Ω—è–º/—Å–ø–∞–¥–∞–Ω–Ω—è–º —Ü—ñ–Ω–∏, —Ä–µ–π—Ç–∏–Ω–≥—É).</li><li>–û–ø—Ç–∏–º—ñ–∑–∞—Ü—ñ—è: <code>useMemo</code> –¥–ª—è —Ä–µ–∑—É–ª—å—Ç–∞—Ç—ñ–≤ —Ñ—ñ–ª—å—Ç—Ä–∞—Ü—ñ—ó/—Å–æ—Ä—Ç—É–≤–∞–Ω–Ω—è, <code>useCallback</code> –¥–ª—è –æ–±—Ä–æ–±–Ω–∏–∫—ñ–≤.</li></ul><h3>–ü—Ä–∏–∫–ª–∞–¥:</h3><pre><code>import React from 'react';\n\nconst productsMock = [\n  { id: 1, name: '–ö—Ä–æ—Å—ñ–≤–∫–∏ A', category: '–í–∑—É—Ç—Ç—è', price: 120, rating: 4.5 },\n  { id: 2, name: '–§—É—Ç–±–æ–ª–∫–∞ B', category: '–û–¥—è–≥', price: 25, rating: 4.0 },\n  { id: 3, name: '–®—Ç–∞–Ω–∏ C', category: '–û–¥—è–≥', price: 60, rating: 4.2 },\n  { id: 4, name: '–ö–µ–ø–∫–∞ D', category: '–ê–∫—Å–µ—Å—É–∞—Ä–∏', price: 15, rating: 3.8 },\n];\n\nfunction useDebounced(value, delay = 300) {\n  const [debounced, setDebounced] = React.useState(value);\n  React.useEffect(() => {\n    const id = setTimeout(() => setDebounced(value), delay);\n    return () => clearTimeout(id);\n  }, [value, delay]);\n  return debounced;\n}\n\nexport default function ProductListApp() {\n  const [query, setQuery] = React.useState('');\n  const [category, setCategory] = React.useState('All');\n  const [sortBy, setSortBy] = React.useState('none');\n\n  const debouncedQuery = useDebounced(query, 250);\n\n  const filtered = React.useMemo(() => {\n    let result = productsMock.slice();\n    if (category !== 'All') result = result.filter(p => p.category === category);\n    if (debouncedQuery) result = result.filter(p => p.name.toLowerCase().includes(debouncedQuery.toLowerCase()));\n    if (sortBy === 'price-asc') result.sort((a,b) => a.price - b.price);\n    if (sortBy === 'price-desc') result.sort((a,b) => b.price - a.price);\n    if (sortBy === 'rating-desc') result.sort((a,b) => b.rating - a.rating);\n    return result;\n  }, [category, debouncedQuery, sortBy]);\n\n  return (\n    <div className='p-6 max-w-3xl mx-auto'>\n      <h2 className='text-2xl font-bold mb-4'>üõçÔ∏è Product List</h2>\n\n      <div className='flex gap-3 mb-4'>\n        <input value={query} onChange={(e) => setQuery(e.target.value)} placeholder='–ü–æ—à—É–∫ —Ç–æ–≤–∞—Ä—ñ–≤...' />\n        <select value={category} onChange={(e) => setCategory(e.target.value)}>\n          <option value='All'>–£—Å—ñ –∫–∞—Ç–µ–≥–æ—Ä—ñ—ó</option>\n          <option value='–í–∑—É—Ç—Ç—è'>–í–∑—É—Ç—Ç—è</option>\n          <option value='–û–¥—è–≥'>–û–¥—è–≥</option>\n          <option value='–ê–∫—Å–µ—Å—É–∞—Ä–∏'>–ê–∫—Å–µ—Å—É–∞—Ä–∏</option>\n        </select>\n        <select value={sortBy} onChange={(e) => setSortBy(e.target.value)}>\n          <option value='none'>–ë–µ–∑ —Å–æ—Ä—Ç—É–≤–∞–Ω–Ω—è</option>\n          <option value='price-asc'>–¶—ñ–Ω–∞: –∑—Ä–æ—Å—Ç–∞–Ω–Ω—è</option>\n          <option value='price-desc'>–¶—ñ–Ω–∞: —Å–ø–∞–¥–∞–Ω–Ω—è</option>\n          <option value='rating-desc'>–†–µ–π—Ç–∏–Ω–≥: —Å–ø–∞–¥–∞–Ω–Ω—è</option>\n        </select>\n      </div>\n\n      <ul className='grid grid-cols-1 gap-3'>\n        {filtered.map(p => (\n          <li key={p.id} className='p-3 border rounded flex justify-between items-center'>\n            <div>\n              <h3 className='font-semibold'>{p.name}</h3>\n              <p className='text-sm text-gray-600'>{p.category} ‚Ä¢ ‚≠ê {p.rating}</p>\n            </div>\n            <div className='text-right'>\n              <div className='font-bold'>{p.price}$</div>\n              <button className='mt-2'>–î–æ–¥–∞—Ç–∏ –≤ –∫–æ—à–∏–∫</button>\n            </div>\n          </li>\n        ))}\n      </ul>\n\n      {filtered.length === 0 && <p className='text-center mt-4 text-gray-500'>–¢–æ–≤–∞—Ä—ñ–≤ –Ω–µ –∑–Ω–∞–π–¥–µ–Ω–æ</p>}\n    </div>\n  );\n}</code></pre><p>‚úÖ <strong>–ú–µ—Ç–∞:</strong> —Å—Ç—É–¥–µ–Ω—Ç–∏ –ø–æ–≤–∏–Ω–Ω—ñ –Ω–∞–≤—á–∏—Ç–∏—Å—è:</p><ul><li>–†–µ–∞–ª—ñ–∑–æ–≤—É–≤–∞—Ç–∏ –ø–æ—à—É–∫, —Ñ—ñ–ª—å—Ç—Ä–∞—Ü—ñ—é —Ç–∞ —Å–æ—Ä—Ç—É–≤–∞–Ω–Ω—è –Ω–∞ —Ñ—Ä–æ–Ω—Ç–µ–Ω–¥—ñ –±–µ–∑ —Å–µ—Ä–≤–µ—Ä–∞.</li><li>–û–ø—Ç–∏–º—ñ–∑—É–≤–∞—Ç–∏ —Ä–µ–Ω–¥–µ—Ä–∏–Ω–≥ —ñ–∑ <code>useMemo</code> —Ç–∞ debounce-–ø–æ—à—É–∫.</li><li>–ü—Ä–æ—î–∫—Ç—É–≤–∞—Ç–∏ –¥–æ—Å—Ç—É–ø–Ω–∏–π —Ç–∞ —ñ–Ω—Ç—É—ó—Ç–∏–≤–Ω–∏–π —ñ–Ω—Ç–µ—Ä—Ñ–µ–π—Å –¥–ª—è –ø–µ—Ä–µ–≥–ª—è–¥—É —Ç–æ–≤–∞—Ä—ñ–≤.</li><li>–ó–∞ –ø–æ—Ç—Ä–µ–±–∏ —ñ–Ω—Ç–µ–≥—Ä—É–≤–∞—Ç–∏ —Ü–µ–π UI –∑ –±–µ–∫–µ–Ω–¥–æ–º –∞–±–æ Tanstack Query –¥–ª—è —Ä–µ–∞–ª—å–Ω–∏—Ö –¥–∞–Ω–∏—Ö.</li></ul><!-- –ë—ñ–±–ª—ñ–æ—Ç–µ–∫–∏: react -->",

    lesson10: '<h2>–£—Ä–æ–∫ 13 ‚Äì –ü–æ—á–∞—Ç–æ–∫ —Ñ—ñ–Ω–∞–ª—å–Ω–æ–≥–æ –ø—Ä–æ—î–∫—Ç—É: —ñ–¥–µ—è, —Å—Ç—Ä—É–∫—Ç—É—Ä–∞, –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–∏ —Ç–∞ —ñ–Ω–¥–∏–≤—ñ–¥—É–∞–ª—å–Ω–∞ —Ä–æ–±–æ—Ç–∞</h2><p>–¶–µ–π —É—Ä–æ–∫ –≤—ñ–¥–∫—Ä–∏–≤–∞—î —Ñ—ñ–Ω–∞–ª—å–Ω–∏–π –µ—Ç–∞–ø –∫—É—Ä—Å—É, –¥–µ –∫–æ–∂–µ–Ω —Å—Ç—É–¥–µ–Ω—Ç —Ä–æ–∑–ø–æ—á–∏–Ω–∞—î —Å—Ç–≤–æ—Ä–µ–Ω–Ω—è –≤–ª–∞—Å–Ω–æ–≥–æ –ø—Ä–æ—î–∫—Ç—É. –ì–æ–ª–æ–≤–Ω–∞ –º–µ—Ç–∞ ‚Äî –∑–∞—Å—Ç–æ—Å—É–≤–∞—Ç–∏ –≤—Å—ñ –≤–∏–≤—á–µ–Ω—ñ —ñ–Ω—Å—Ç—Ä—É–º–µ–Ω—Ç–∏ (React, Zustand, Tanstack Query, Redux Toolkit, Framer Motion, dnd-kit —Ç–æ—â–æ) —É —Ä–µ–∞–ª—å–Ω–æ–º—É –ø—Ä–æ—î–∫—Ç—ñ, —â–æ –º–∞—î —á—ñ—Ç–∫—É —Å—Ç—Ä—É–∫—Ç—É—Ä—É, —Ñ—É–Ω–∫—Ü—ñ–æ–Ω–∞–ª—å–Ω—ñ—Å—Ç—å —ñ –ø—Ä–∏–≤–∞–±–ª–∏–≤–∏–π UI.</p><h3>–û—Å–Ω–æ–≤–Ω—ñ –µ—Ç–∞–ø–∏:</h3><ul><li><strong>–í–∏–±—ñ—Ä —ñ–¥–µ—ó:</strong> —Å—Ç—É–¥–µ–Ω—Ç —Å–∞–º–æ—Å—Ç—ñ–π–Ω–æ –æ–±–∏—Ä–∞—î —Ç–µ–º—É –∑–∞—Å—Ç–æ—Å—É–Ω–∫—É (–Ω–∞–ø—Ä–∏–∫–ª–∞–¥, Task Board, Weather App, Expense Tracker, Movie Explorer —Ç–æ—â–æ).</li><li><strong>–ü—Ä–æ—î–∫—Ç—É–≤–∞–Ω–Ω—è —Å—Ç—Ä—É–∫—Ç—É—Ä–∏:</strong> –≤–∏–∑–Ω–∞—á–µ–Ω–Ω—è –∫–æ–º–ø–æ–Ω–µ–Ω—Ç—ñ–≤, –º–∞—Ä—à—Ä—É—Ç—ñ–≤, —Å—Ç–∞–Ω—É, –æ—Å–Ω–æ–≤–Ω–∏—Ö —Å—Ç–æ—Ä—ñ–Ω–æ–∫.</li><li><strong>–ü–æ–±—É–¥–æ–≤–∞ UI:</strong> –≤–∏–∫–æ—Ä–∏—Å—Ç–∞–Ω–Ω—è –±—ñ–±–ª—ñ–æ—Ç–µ–∫ ShadCN UI, Tailwind CSS, Framer Motion –¥–ª—è —Å—Ç–≤–æ—Ä–µ–Ω–Ω—è —Å—É—á–∞—Å–Ω–æ–≥–æ —ñ–Ω—Ç–µ—Ä—Ñ–µ–π—Å—É.</li><li><strong>–Ü–Ω–¥–∏–≤—ñ–¥—É–∞–ª—å–Ω–∞ —Ä–æ–±–æ—Ç–∞:</strong> –∫–æ–∂–µ–Ω —Å—Ç—É–¥–µ–Ω—Ç —Ä–µ–∞–ª—ñ–∑—É—î –≤–ª–∞—Å–Ω—É –ª–æ–≥—ñ–∫—É, –¥–∏–∑–∞–π–Ω —ñ —Å—Ç—Ä—É–∫—Ç—É—Ä—É –∑ —É—Ä–∞—Ö—É–≤–∞–Ω–Ω—è–º –≤–∏–º–æ–≥.</li></ul><h3>–ü—Ä–∏–∫–ª–∞–¥ —Å—Ç—Ä—É–∫—Ç—É—Ä–∏ –ø—Ä–æ—î–∫—Ç—É:</h3><pre><code>src/\n ‚îú‚îÄ‚îÄ components/\n ‚îÇ    ‚îú‚îÄ‚îÄ Header.jsx\n ‚îÇ    ‚îú‚îÄ‚îÄ Sidebar.jsx\n ‚îÇ    ‚îî‚îÄ‚îÄ ItemCard.jsx\n ‚îú‚îÄ‚îÄ pages/\n ‚îÇ    ‚îú‚îÄ‚îÄ Home.jsx\n ‚îÇ    ‚îú‚îÄ‚îÄ Details.jsx\n ‚îÇ    ‚îî‚îÄ‚îÄ Settings.jsx\n ‚îú‚îÄ‚îÄ store/\n ‚îÇ    ‚îî‚îÄ‚îÄ useAppStore.js\n ‚îú‚îÄ‚îÄ hooks/\n ‚îÇ    ‚îî‚îÄ‚îÄ useFetchData.js\n ‚îú‚îÄ‚îÄ App.jsx\n ‚îî‚îÄ‚îÄ main.jsx</code></pre><h3>–ü–æ—Ä–∞–¥–∏:</h3><ul><li>–ü–æ—á–Ω—ñ—Ç—å —ñ–∑ <strong>–º—ñ–Ω—ñ–º–∞–ª—å–Ω–æ–≥–æ —Ñ—É–Ω–∫—Ü—ñ–æ–Ω–∞–ª—É (MVP)</strong>, –ø–æ—Å—Ç—É–ø–æ–≤–æ –¥–æ–¥–∞—é—á–∏ –Ω–æ–≤—ñ –º–æ–∂–ª–∏–≤–æ—Å—Ç—ñ.</li><li>–°—Ç–≤–æ—Ä—ñ—Ç—å –±–∞–∑–æ–≤—É –Ω–∞–≤—ñ–≥–∞—Ü—ñ—é —Ç–∞ —Å—Ç—Ä—É–∫—Ç—É—Ä—É –∫–æ–º–ø–æ–Ω–µ–Ω—Ç—ñ–≤ –¥–æ –ø–æ—á–∞—Ç–∫—É —Å—Ç–∏–ª—ñ–∑–∞—Ü—ñ—ó.</li><li>–ü—Ä–æ–¥—É–º–∞–π—Ç–µ –∫–µ—Ä—É–≤–∞–Ω–Ω—è —Å—Ç–∞–Ω–æ–º –∑–∞–∑–¥–∞–ª–µ–≥—ñ–¥—å (Zustand –∞–±–æ Redux Toolkit).</li><li>–î–æ–∫—É–º–µ–Ω—Ç—É–π—Ç–µ —Å–≤—ñ–π –∫–æ–¥ —ñ —Ä–æ–∑–¥—ñ–ª—ñ—Ç—å –π–æ–≥–æ –Ω–∞ –ª–æ–≥—ñ—á–Ω—ñ —á–∞—Å—Ç–∏–Ω–∏.</li></ul><p>‚úÖ <strong>–ú–µ—Ç–∞:</strong> —Å—Ç—É–¥–µ–Ω—Ç–∏ –ø–æ–≤–∏–Ω–Ω—ñ:</p><ul><li>–†–æ–∑—Ä–æ–±–∏—Ç–∏ —ñ–Ω–¥–∏–≤—ñ–¥—É–∞–ª—å–Ω–∏–π –ø–ª–∞–Ω —ñ–¥–µ—ó —Å–≤–æ–≥–æ –∑–∞—Å—Ç–æ—Å—É–Ω–∫—É.</li><li>–ü–æ–±—É–¥—É–≤–∞—Ç–∏ –±–∞–∑–æ–≤—É —Å—Ç—Ä—É–∫—Ç—É—Ä—É React-–ø—Ä–æ—î–∫—Ç—É –∑ –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–∞–º–∏.</li><li>–ü—ñ–¥–≥–æ—Ç—É–≤–∞—Ç–∏ –æ—Å–Ω–æ–≤—É –¥–ª—è —ñ–Ω—Ç–µ–≥—Ä–∞—Ü—ñ—ó —Å—Ç–∞–Ω—É, API, –∞–Ω—ñ–º–∞—Ü—ñ–π —Ç–∞ UI-–∫–æ–º–ø–æ–Ω–µ–Ω—Ç—ñ–≤.</li><li>–†–æ–∑–ø–æ—á–∞—Ç–∏ –∞–∫—Ç–∏–≤–Ω—É —Ñ–∞–∑—É —Å—Ç–≤–æ—Ä–µ–Ω–Ω—è —Ñ—ñ–Ω–∞–ª—å–Ω–æ–≥–æ –ø—Ä–æ—î–∫—Ç—É.</li></ul><!-- –¢–∏–ø —É—Ä–æ–∫—É: –ø—Ä–∞–∫—Ç–∏—á–Ω–∏–π, –ø—ñ–¥–≥–æ—Ç–æ–≤–∫–∞ –¥–æ —Ñ—ñ–Ω–∞–ª—å–Ω–æ–≥–æ –ø—Ä–æ—î–∫—Ç—É -->',

    lesson11: "<h2>–£—Ä–æ–∫ 14 ‚Äì Speed Booster: –æ–ø—Ç–∏–º—ñ–∑–∞—Ü—ñ—è –ø—Ä–æ–¥—É–∫—Ç–∏–≤–Ω–æ—Å—Ç—ñ —á–µ—Ä–µ–∑ lazy loading, –º–µ–º–æ—ñ–∑–∞—Ü—ñ—é —Ç–∞ Suspense</h2><p>–£ —Ü—å–æ–º—É —É—Ä–æ—Ü—ñ —Å—Ç—É–¥–µ–Ω—Ç–∏ –¥—ñ–∑–Ω–∞—é—Ç—å—Å—è, —è–∫ –ø—ñ–¥–≤–∏—â–∏—Ç–∏ –ø—Ä–æ–¥—É–∫—Ç–∏–≤–Ω—ñ—Å—Ç—å React-–∑–∞—Å—Ç–æ—Å—É–Ω–∫—ñ–≤ –∑–∞ –¥–æ–ø–æ–º–æ–≥–æ—é <strong>lazy loading</strong>, <strong>React.memo</strong>, <strong>useMemo</strong>, <strong>useCallback</strong> —ñ <strong>Suspense</strong>. –¶—ñ —ñ–Ω—Å—Ç—Ä—É–º–µ–Ω—Ç–∏ –¥–æ–ø–æ–º–∞–≥–∞—é—Ç—å —Å–∫–æ—Ä–æ—Ç–∏—Ç–∏ —á–∞—Å –∑–∞–≤–∞–Ω—Ç–∞–∂–µ–Ω–Ω—è, –∑–º–µ–Ω—à–∏—Ç–∏ –∫—ñ–ª—å–∫—ñ—Å—Ç—å –Ω–µ–ø–æ—Ç—Ä—ñ–±–Ω–∏—Ö —Ä–µ–Ω–¥–µ—Ä—ñ–≤ —ñ –∑—Ä–æ–±–∏—Ç–∏ —Ä–æ–±–æ—Ç—É —ñ–Ω—Ç–µ—Ä—Ñ–µ–π—Å—É –ø–ª–∞–≤–Ω—ñ—à–æ—é.</p><h3>–û—Å–Ω–æ–≤–Ω—ñ –º–æ–∂–ª–∏–≤–æ—Å—Ç—ñ:</h3><ul><li>–í–∏–∫–æ—Ä–∏—Å—Ç–∞–Ω–Ω—è <code>React.lazy()</code> —ñ <code>Suspense</code> –¥–ª—è –¥–∏–Ω–∞–º—ñ—á–Ω–æ–≥–æ —ñ–º–ø–æ—Ä—Ç—É –∫–æ–º–ø–æ–Ω–µ–Ω—Ç—ñ–≤.</li><li>–û–ø—Ç–∏–º—ñ–∑–∞—Ü—ñ—è –ø–æ–≤—Ç–æ—Ä–Ω–∏—Ö —Ä–µ–Ω–¥–µ—Ä—ñ–≤ —á–µ—Ä–µ–∑ <code>React.memo</code>.</li><li>–ú–µ–º–æ—ñ–∑–∞—Ü—ñ—è –æ–±—á–∏—Å–ª–µ–Ω—å —ñ –∫–æ–ª–±–µ–∫—ñ–≤ (<code>useMemo</code>, <code>useCallback</code>).</li><li>–ü–æ–∫—Ä–∞—â–µ–Ω–Ω—è UX —á–µ—Ä–µ–∑ Skeleton –∞–±–æ fallback-–∑–∞–≤–∞–Ω—Ç–∞–∂—É–≤–∞—á—ñ.</li></ul><h3>–ü—Ä–∏–∫–ª–∞–¥:</h3><pre><code>import React, { Suspense, useState, useMemo, useCallback } from 'react';\n\nconst HeavyChart = React.lazy(() => import('./HeavyChart'));\n\nfunction ExpensiveList({ items }) {\n  console.log('–†–µ–Ω–¥–µ—Ä —Å–ø–∏—Å–∫—É');\n  const sorted = useMemo(() => [...items].sort(), [items]);\n  return <ul>{sorted.map((i) => <li key={i}>{i}</li>)}</ul>;\n}\n\nconst MemoList = React.memo(ExpensiveList);\n\nexport default function SpeedBooster() {\n  const [count, setCount] = useState(0);\n  const [data] = useState(['–ë–∞–Ω–∞–Ω', '–ê–ø–µ–ª—å—Å–∏–Ω', '–Ø–±–ª—É–∫–æ']);\n\n  const increment = useCallback(() => setCount((c) => c + 1), []);\n\n  return (\n    <div className='p-6 text-center'>\n      <h2 className='text-2xl font-bold mb-4'>‚ö° Speed Booster</h2>\n      <button onClick={increment} className='mb-4'>–ö–ª—ñ–∫–Ω–∏ ({count})</button>\n\n      <Suspense fallback={<p>–ó–∞–≤–∞–Ω—Ç–∞–∂–µ–Ω–Ω—è –≥—Ä–∞—Ñ—ñ–∫–∞...</p>}>\n        <HeavyChart />\n      </Suspense>\n\n      <MemoList items={data} />\n    </div>\n  );\n}</code></pre><p>‚úÖ <strong>–ú–µ—Ç–∞:</strong> —Å—Ç—É–¥–µ–Ω—Ç–∏ –ø–æ–≤–∏–Ω–Ω—ñ –Ω–∞–≤—á–∏—Ç–∏—Å—è:</p><ul><li>–í–∏–∫–æ—Ä–∏—Å—Ç–æ–≤—É–≤–∞—Ç–∏ lazy loading –¥–ª—è –∞—Å–∏–Ω—Ö—Ä–æ–Ω–Ω–æ–≥–æ –ø—ñ–¥–≤–∞–Ω—Ç–∞–∂–µ–Ω–Ω—è –∫–æ–º–ø–æ–Ω–µ–Ω—Ç—ñ–≤.</li><li>–ó–∞—Å—Ç–æ—Å–æ–≤—É–≤–∞—Ç–∏ –º–µ–º–æ—ñ–∑–∞—Ü—ñ—é –¥–ª—è –æ–ø—Ç–∏–º—ñ–∑–∞—Ü—ñ—ó –æ–±—á–∏—Å–ª–µ–Ω—å —ñ —É–Ω–∏–∫–Ω–µ–Ω–Ω—è –∑–∞–π–≤–∏—Ö —Ä–µ–Ω–¥–µ—Ä—ñ–≤.</li><li>–ü—Ä–∞—Ü—é–≤–∞—Ç–∏ –∑ <code>Suspense</code> —ñ fallback-—Å—Ç–∞–Ω–∞–º–∏ –¥–ª—è –ø–æ–∫—Ä–∞—â–µ–Ω–Ω—è UX.</li><li>–†–æ–∑—É–º—ñ—Ç–∏ —Ä—ñ–∑–Ω–∏—Ü—é –º—ñ–∂ –æ–ø—Ç–∏–º—ñ–∑–∞—Ü—ñ—î—é —Ä–µ–Ω–¥–µ—Ä—ñ–≤ —Ç–∞ –æ–ø—Ç–∏–º—ñ–∑–∞—Ü—ñ—î—é –æ–±—á–∏—Å–ª–µ–Ω—å.</li></ul><!-- –ë—ñ–±–ª—ñ–æ—Ç–µ–∫–∞: react -->",

    lesson12: "<h2>–£—Ä–æ–∫ 15 ‚Äì Booking Form App: –±–∞–≥–∞—Ç–æ–∫—Ä–æ–∫–æ–≤–∞ —Ñ–æ—Ä–º–∞, UX-—Ñ–ª–æ—É —Ç–∞ –ø–µ—Ä–µ–≤—ñ—Ä–∫–∏ –¥–∞–Ω–∏—Ö</h2><p>–£ —Ü—å–æ–º—É —É—Ä–æ—Ü—ñ —Å—Ç—É–¥–µ–Ω—Ç–∏ —Å—Ç–≤–æ—Ä—è—Ç—å <strong>Booking Form App</strong> ‚Äî —ñ–Ω—Ç–µ—Ä–∞–∫—Ç–∏–≤–Ω—É –±–∞–≥–∞—Ç–æ–∫—Ä–æ–∫–æ–≤—É —Ñ–æ—Ä–º—É –¥–ª—è –±—Ä–æ–Ω—é–≤–∞–Ω–Ω—è (–Ω–∞–ø—Ä–∏–∫–ª–∞–¥, –ø–æ–¥–æ—Ä–æ–∂–µ–π, –∫–≤–∏—Ç–∫—ñ–≤ –∞–±–æ –≥–æ—Ç–µ–ª—ñ–≤). –û—Å–Ω–æ–≤–Ω–∞ –º–µ—Ç–∞ ‚Äî –Ω–∞–≤—á–∏—Ç–∏—Å—è —Å—Ç–≤–æ—Ä—é–≤–∞—Ç–∏ –∑—Ä–æ–∑—É–º—ñ–ª–∏–π UX-—Ñ–ª–æ—É, –ø–µ—Ä–µ–≤—ñ—Ä—è—Ç–∏ –≤–≤–µ–¥–µ–Ω—ñ –¥–∞–Ω—ñ —Ç–∞ –ø—Ä–∞—Ü—é–≤–∞—Ç–∏ –∑ –ª–æ–∫–∞–ª—å–Ω–∏–º —Å—Ç–∞–Ω–æ–º –¥–ª—è –∑–±–µ—Ä–µ–∂–µ–Ω–Ω—è –ø—Ä–æ–≥—Ä–µ—Å—É –∫–æ—Ä–∏—Å—Ç—É–≤–∞—á–∞.</p><h3>–û—Å–Ω–æ–≤–Ω—ñ –º–æ–∂–ª–∏–≤–æ—Å—Ç—ñ:</h3><ul><li>–ë–∞–≥–∞—Ç–æ–∫—Ä–æ–∫–æ–≤–∞ —Ñ–æ—Ä–º–∞ –∑ –ø–µ—Ä–µ—Ö–æ–¥–∞–º–∏ –º—ñ–∂ –µ—Ç–∞–ø–∞–º–∏.</li><li>–ü–µ—Ä–µ–≤—ñ—Ä–∫–∞ –¥–∞–Ω–∏—Ö –∫–æ—Ä–∏—Å—Ç—É–≤–∞—á–∞ –ø–µ—Ä–µ–¥ –ø–µ—Ä–µ—Ö–æ–¥–æ–º –¥–æ –Ω–∞—Å—Ç—É–ø–Ω–æ–≥–æ –∫—Ä–æ–∫—É.</li><li>–ó–±–µ—Ä–µ–∂–µ–Ω–Ω—è —Å—Ç–∞–Ω—É –≤–≤–µ–¥–µ–Ω–∏—Ö –¥–∞–Ω–∏—Ö –º—ñ–∂ –∫—Ä–æ–∫–∞–º–∏.</li><li>–í–∏–∫–æ—Ä–∏—Å—Ç–∞–Ω–Ω—è –∫–æ–º–ø–æ–Ω–µ–Ω—Ç—ñ–≤ –¥–ª—è –ø–æ–ª—ñ–≤, –∫–Ω–æ–ø–æ–∫ —ñ –ø—Ä–æ–≥—Ä–µ—Å—É –∑–∞–ø–æ–≤–Ω–µ–Ω–Ω—è.</li></ul><h3>–ü—Ä–∏–∫–ª–∞–¥:</h3><pre><code>import React, { useState } from 'react';\n\nfunction Step1({ next, data, setData }) {\n  return (\n    <div>\n      <h3>–ö—Ä–æ–∫ 1: –ü–µ—Ä—Å–æ–Ω–∞–ª—å–Ω—ñ –¥–∞–Ω—ñ</h3>\n      <input placeholder='–Ü–º º—è' value={data.name} onChange={(e) => setData({ ...data, name: e.target.value })} />\n      <input placeholder='Email' value={data.email} onChange={(e) => setData({ ...data, email: e.target.value })} />\n      <button disabled={!data.name || !data.email} onClick={next}>–î–∞–ª—ñ</button>\n    </div>\n  );\n}\n\nfunction Step2({ next, back, data, setData }) {\n  return (\n    <div>\n      <h3>–ö—Ä–æ–∫ 2: –î–µ—Ç–∞–ª—ñ –±—Ä–æ–Ω—é–≤–∞–Ω–Ω—è</h3>\n      <select value={data.room} onChange={(e) => setData({ ...data, room: e.target.value })}>\n        <option value=''>–û–±–µ—Ä—ñ—Ç—å —Ç–∏–ø –Ω–æ–º–µ—Ä–∞</option>\n        <option value='single'>–û–¥–Ω–æ–º—ñ—Å–Ω–∏–π</option>\n        <option value='double'>–î–≤–æ–º—ñ—Å–Ω–∏–π</option>\n      </select>\n      <button onClick={back}>–ù–∞–∑–∞–¥</button>\n      <button disabled={!data.room} onClick={next}>–î–∞–ª—ñ</button>\n    </div>\n  );\n}\n\nfunction Step3({ back, data }) {\n  return (\n    <div>\n      <h3>–ö—Ä–æ–∫ 3: –ü—ñ–¥—Ç–≤–µ—Ä–¥–∂–µ–Ω–Ω—è</h3>\n      <p><strong>–Ü–º º—è:</strong> {data.name}</p>\n      <p><strong>Email:</strong> {data.email}</p>\n      <p><strong>–¢–∏–ø –Ω–æ–º–µ—Ä–∞:</strong> {data.room}</p>\n      <button onClick={back}>–ù–∞–∑–∞–¥</button>\n      <button onClick={() => alert('–ë—Ä–æ–Ω—é–≤–∞–Ω–Ω—è –ø—ñ–¥—Ç–≤–µ—Ä–¥–∂–µ–Ω–æ!')}>–ü—ñ–¥—Ç–≤–µ—Ä–¥–∏—Ç–∏</button>\n    </div>\n  );\n}\n\nexport default function BookingFormApp() {\n  const [step, setStep] = useState(1);\n  const [data, setData] = useState({ name: '', email: '', room: '' });\n\n  return (\n    <div className='p-6 max-w-md mx-auto text-center'>\n      <h2 className='text-2xl font-bold mb-4'>üßæ Booking Form</h2>\n      <div className='mb-3'>–ö—Ä–æ–∫ {step} —ñ–∑ 3</div>\n      {step === 1 && <Step1 next={() => setStep(2)} data={data} setData={setData} />}\n      {step === 2 && <Step2 next={() => setStep(3)} back={() => setStep(1)} data={data} setData={setData} />}\n      {step === 3 && <Step3 back={() => setStep(2)} data={data} />}\n    </div>\n  );\n}</code></pre><p>‚úÖ <strong>–ú–µ—Ç–∞:</strong> —Å—Ç—É–¥–µ–Ω—Ç–∏ –ø–æ–≤–∏–Ω–Ω—ñ –Ω–∞–≤—á–∏—Ç–∏—Å—è:</p><ul><li>–°—Ç–≤–æ—Ä—é–≤–∞—Ç–∏ –±–∞–≥–∞—Ç–æ–∫—Ä–æ–∫–æ–≤—ñ —Ñ–æ—Ä–º–∏ –∑ –∫–æ–Ω—Ç—Ä–æ–ª–µ–º –ø–µ—Ä–µ—Ö–æ–¥—ñ–≤.</li><li>–û—Ä–≥–∞–Ω—ñ–∑–æ–≤—É–≤–∞—Ç–∏ UX-—Ñ–ª–æ—É —ñ–∑ –∑–±–µ—Ä–µ–∂–µ–Ω–Ω—è–º –≤–≤–µ–¥–µ–Ω–∏—Ö –¥–∞–Ω–∏—Ö.</li><li>–í–∏–∫–æ–Ω—É–≤–∞—Ç–∏ –ø–µ—Ä–µ–≤—ñ—Ä–∫—É –ø–æ–ª—ñ–≤ –ø–µ—Ä–µ–¥ –ø–µ—Ä–µ—Ö–æ–¥–æ–º –¥–æ –Ω–∞—Å—Ç—É–ø–Ω–æ–≥–æ –∫—Ä–æ–∫—É.</li><li>–ü–ª–∞–Ω—É–≤–∞—Ç–∏ —Å—Ç—Ä—É–∫—Ç—É—Ä—É –∫–æ–º–ø–æ–Ω–µ–Ω—Ç—ñ–≤ –¥–ª—è –≤–µ–ª–∏–∫–∏—Ö —Ñ–æ—Ä–º.</li></ul><!-- –ë—ñ–±–ª—ñ–æ—Ç–µ–∫–∞: react -->",

    lesson13: '<h2>–£—Ä–æ–∫ 16 ‚Äì –ü—Ä–µ–∑–µ–Ω—Ç–∞—Ü—ñ—è —Ñ—ñ–Ω–∞–ª—å–Ω–æ–≥–æ –ø—Ä–æ—î–∫—Ç—É: –¥–µ–º–æ–Ω—Å—Ç—Ä–∞—Ü—ñ—è, –ø–µ—Ä–µ–≤—ñ—Ä–∫–∞ —Ç–∞ —Ñ—ñ–¥–±–µ–∫</h2><p>–¶–µ–π –∑–∞–∫–ª—é—á–Ω–∏–π —É—Ä–æ–∫ –ø—Ä–∏—Å–≤—è—á–µ–Ω–∏–π <strong>–ø—Ä–µ–∑–µ–Ω—Ç–∞—Ü—ñ—ó —Ñ—ñ–Ω–∞–ª—å–Ω–æ–≥–æ –ø—Ä–æ—î–∫—Ç—É</strong>. –°—Ç—É–¥–µ–Ω—Ç–∏ –¥–µ–º–æ–Ω—Å—Ç—Ä—É—é—Ç—å —Å–≤–æ—ó —Ä–æ–±–æ—Ç–∏, –ø–æ–∫–∞–∑—É—é—Ç—å —Ñ—É–Ω–∫—Ü—ñ–æ–Ω–∞–ª, –∞—Ä—Ö—ñ—Ç–µ–∫—Ç—É—Ä—É, –¥–∏–∑–∞–π–Ω —ñ —Ç–µ—Ö–Ω–æ–ª–æ–≥—ñ—ó, —è–∫—ñ –≤–æ–Ω–∏ –∑–∞—Å—Ç–æ—Å—É–≤–∞–ª–∏. –û—Å–Ω–æ–≤–Ω–∞ –º–µ—Ç–∞ ‚Äî –æ—Ü—ñ–Ω–∏—Ç–∏ —Ä–µ–∑—É–ª—å—Ç–∞—Ç–∏ –Ω–∞–≤—á–∞–Ω–Ω—è, –æ—Ç—Ä–∏–º–∞—Ç–∏ –∑–≤–æ—Ä–æ—Ç–Ω–∏–π –∑–≤‚Äô—è–∑–æ–∫ —ñ –ø—ñ–¥—Å—É–º—É–≤–∞—Ç–∏ –≤–µ—Å—å –∫—É—Ä—Å.</p><h3>–û—Å–Ω–æ–≤–Ω—ñ –µ—Ç–∞–ø–∏:</h3><ul><li><strong>–ü—Ä–µ–∑–µ–Ω—Ç–∞—Ü—ñ—è –ø—Ä–æ—î–∫—Ç—É:</strong> –∫–æ—Ä–æ—Ç–∫–∏–π –æ–≥–ª—è–¥ —ñ–¥–µ—ó, –ø–æ–∫–∞–∑ –≥–æ–ª–æ–≤–Ω–∏—Ö –º–æ–∂–ª–∏–≤–æ—Å—Ç–µ–π —ñ –∫–æ—Ä–∏—Å—Ç—É–≤–∞—Ü—å–∫–æ–≥–æ —Ñ–ª–æ—É.</li><li><strong>–î–µ–º–æ–Ω—Å—Ç—Ä–∞—Ü—ñ—è –∫–æ–¥—É:</strong> –ø–æ—è—Å–Ω–µ–Ω–Ω—è —Å—Ç—Ä—É–∫—Ç—É—Ä–∏, –æ—Ä–≥–∞–Ω—ñ–∑–∞—Ü—ñ—ó —Å—Ç–∞–Ω—É, –≤–∏–∫–æ—Ä–∏—Å—Ç–∞–Ω–Ω—è –±—ñ–±–ª—ñ–æ—Ç–µ–∫ —ñ –ø–∞—Ç–µ—Ä–Ω—ñ–≤.</li><li><strong>–ü–µ—Ä–µ–≤—ñ—Ä–∫–∞ –ø—Ä–æ—î–∫—Ç—É:</strong> —Ç–µ—Å—Ç—É–≤–∞–Ω–Ω—è –æ—Å–Ω–æ–≤–Ω–æ–≥–æ —Ñ—É–Ω–∫—Ü—ñ–æ–Ω–∞–ª—É, UI/UX —Ç–∞ –∞–¥–∞–ø—Ç–∏–≤–Ω–æ—Å—Ç—ñ.</li><li><strong>–§—ñ–¥–±–µ–∫:</strong> –æ—Ç—Ä–∏–º–∞–Ω–Ω—è –ø–æ—Ä–∞–¥ –≤—ñ–¥ –≤–∏–∫–ª–∞–¥–∞—á–∞ —Ç–∞ –∫–æ–ª–µ–≥, –æ–±–≥–æ–≤–æ—Ä–µ–Ω–Ω—è –º–æ–∂–ª–∏–≤–∏—Ö –ø–æ–∫—Ä–∞—â–µ–Ω—å.</li></ul><h3>–ü—Ä–∏–∫–ª–∞–¥ —Å—Ç—Ä—É–∫—Ç—É—Ä–∏ –ø—Ä–µ–∑–µ–Ω—Ç–∞—Ü—ñ—ó:</h3><pre><code>1Ô∏è‚É£ –Ü–¥–µ—è: –∫–æ—Ä–æ—Ç–∫–∏–π –æ–ø–∏—Å –ø—Ä–æ–±–ª–µ–º–∏, —è–∫—É –≤–∏—Ä—ñ—à—É—î –∑–∞—Å—Ç–æ—Å—É–Ω–æ–∫.\n2Ô∏è‚É£ –§—É–Ω–∫—Ü—ñ–æ–Ω–∞–ª: —â–æ –º–æ–∂–µ –∫–æ—Ä–∏—Å—Ç—É–≤–∞—á –∑—Ä–æ–±–∏—Ç–∏ –≤ –∑–∞—Å—Ç–æ—Å—É–Ω–∫—É.\n3Ô∏è‚É£ –¢–µ—Ö–Ω—ñ—á–Ω–∞ —á–∞—Å—Ç–∏–Ω–∞: –±—ñ–±–ª—ñ–æ—Ç–µ–∫–∏, —ñ–Ω—Å—Ç—Ä—É–º–µ–Ω—Ç–∏, —Å—Ç—Ä—É–∫—Ç—É—Ä–∞ –∫–æ–º–ø–æ–Ω–µ–Ω—Ç—ñ–≤.\n4Ô∏è‚É£ –î–µ–º–æ–Ω—Å—Ç—Ä–∞—Ü—ñ—è: –ø–æ–∫–∞–∑ UI —ñ –æ—Å–Ω–æ–≤–Ω–∏—Ö —Å—Ü–µ–Ω–∞—Ä—ñ—ó–≤.\n5Ô∏è‚É£ –ü—ñ–¥—Å—É–º–∫–∏: —á–æ–≥–æ –≤–¥–∞–ª–æ—Å—è –Ω–∞–≤—á–∏—Ç–∏—Å—è —Ç–∞ —â–æ –º–æ–∂–Ω–∞ –≤–¥–æ—Å–∫–æ–Ω–∞–ª–∏—Ç–∏.</code></pre><h3>–ü–æ—Ä–∞–¥–∏ –¥–ª—è –ø—Ä–µ–∑–µ–Ω—Ç–∞—Ü—ñ—ó:</h3><ul><li>–ü—ñ–¥–≥–æ—Ç—É–π—Ç–µ –∫–æ—Ä–æ—Ç–∫–∏–π —Å—Ü–µ–Ω–∞—Ä—ñ–π –¥–µ–º–æ–Ω—Å—Ç—Ä–∞—Ü—ñ—ó (–¥–æ 5 —Ö–≤–∏–ª–∏–Ω).</li><li>–ü–æ–∫–∞–∂—ñ—Ç—å –∫–ª—é—á–æ–≤—ñ —Ñ—ñ—á—ñ, –∞ –Ω–µ –≤–µ—Å—å –∫–æ–¥.</li><li>–ó–≤–µ—Ä—Ç–∞–π—Ç–µ —É–≤–∞–≥—É –Ω–∞ UX, –∞–Ω—ñ–º–∞—Ü—ñ—ó —Ç–∞ –ø–ª–∞–≤–Ω—ñ—Å—Ç—å —Ä–æ–±–æ—Ç–∏ –∑–∞—Å—Ç–æ—Å—É–Ω–∫—É.</li><li>–ü—Ä–æ–¥–µ–º–æ–Ω—Å—Ç—Ä—É–π—Ç–µ, —è–∫ –≤–∏ –∑–∞—Å—Ç–æ—Å—É–≤–∞–ª–∏ React, Zustand, Redux Toolkit, Tanstack Query –∞–±–æ —ñ–Ω—à—ñ —ñ–Ω—Å—Ç—Ä—É–º–µ–Ω—Ç–∏ –∑ –∫—É—Ä—Å—É.</li></ul><p>‚úÖ <strong>–ú–µ—Ç–∞:</strong> —Å—Ç—É–¥–µ–Ω—Ç–∏ –ø–æ–≤–∏–Ω–Ω—ñ:</p><ul><li>–ü—Ä–µ–¥—Å—Ç–∞–≤–∏—Ç–∏ —Å–≤—ñ–π —Ñ—ñ–Ω–∞–ª—å–Ω–∏–π –ø—Ä–æ—î–∫—Ç –ø—É–±–ª—ñ—á–Ω–æ.</li><li>–ü–æ—è—Å–Ω–∏—Ç–∏ —Ç–µ—Ö–Ω—ñ—á–Ω—ñ —Ä—ñ—à–µ–Ω–Ω—è, —è–∫—ñ –≤–æ–Ω–∏ –ø—Ä–∏–π–º–∞–ª–∏ –ø—ñ–¥ —á–∞—Å —Ä–æ–∑—Ä–æ–±–∫–∏.</li><li>–û—Ç—Ä–∏–º–∞—Ç–∏ –∫–æ–Ω—Å—Ç—Ä—É–∫—Ç–∏–≤–Ω–∏–π —Ñ—ñ–¥–±–µ–∫ —ñ –∑—Ä–æ–∑—É–º—ñ—Ç–∏ –Ω–∞–ø—Ä—è–º–∏ –¥–ª—è —Ä–æ–∑–≤–∏—Ç–∫—É.</li><li>–ü—ñ–¥—Å—É–º—É–≤–∞—Ç–∏ —Å–≤—ñ–π —à–ª—è—Ö —É –∫—É—Ä—Å—ñ —Ç–∞ –≤–∏–∑–Ω–∞—á–∏—Ç–∏ –Ω–∞—Å—Ç—É–ø–Ω—ñ –∫—Ä–æ–∫–∏ —É –≤–∏–≤—á–µ–Ω–Ω—ñ —Ñ—Ä–æ–Ω—Ç–µ–Ω–¥—É.</li></ul><!-- –¢–∏–ø —É—Ä–æ–∫—É: –ø—Ä–µ–∑–µ–Ω—Ç–∞—Ü—ñ–π–Ω–∏–π, –∑–∞–≤–µ—Ä—à–∞–ª—å–Ω–∏–π -->',
};
const lessonsContainer = document.getElementById("lessons-grid");
const viewer = document.getElementById("lesson-viewer");
const viewerTitle = document.getElementById("viewer-title");
const viewerText = document.getElementById("viewer-text");
const backBtn = document.getElementById("back-btn");

Object.keys(lessonsContent).forEach(lessonId => {
    const fileDiv = document.createElement("div");
    fileDiv.className = "file";
    fileDiv.dataset.id = lessonId;

    const tempDiv = document.createElement("div");
    tempDiv.innerHTML = lessonsContent[lessonId];
    const title = tempDiv.querySelector("h2") ?.textContent || lessonId;

    fileDiv.textContent = `üìÑ ${title}`;
    lessonsContainer.appendChild(fileDiv);

    fileDiv.addEventListener("click", () => {
        viewerTitle.textContent = title;
        viewerText.innerHTML = lessonsContent[lessonId];
        lessonsContainer.classList.add("hidden");
        viewer.classList.remove("hidden");
    });
});

backBtn.addEventListener("click", () => {
    viewer.classList.add("hidden");
    lessonsContainer.classList.remove("hidden");
});